INSERT INTO "zhishione" VALUES (1, '“床前明月光，疑是地上霜。”——《静夜思》,出自哪位诗人的作品？', '王维', '杜甫', '李白', '', 'C', '此句出自唐代诗人李白的《静夜思》。');
INSERT INTO "zhishione" VALUES (2, '“春眠不觉晓，处处闻啼鸟。”——《春晓》,出自哪位诗人的作品？', '李白', '孟浩然', '杜甫', '', 'B', '此句出自唐代诗人孟浩然的《春晓》,描述了春天早晨的宁静与鸟鸣。');
INSERT INTO "zhishione" VALUES (3, '“野火烧不尽，春风吹又生。”——《赋得古原草送别》,出自哪位诗人的作品？', '白居易', '王维', '李商隐', '', 'A', '此句出自唐代诗人白居易的《赋得古原草送别》。');
INSERT INTO "zhishione" VALUES (4, '“白日依山尽，黄河入海流。”——《登鹳雀楼》,出自哪位诗人的作品？', '王之涣', '李白', '杜甫', '', 'A', '此句出自唐代诗人王之涣的《登鹳雀楼》。');
INSERT INTO "zhishione" VALUES (5, '“飞流直下三千尺，疑是银河落九天。”——《望庐山瀑布》,出自哪位诗人的作品？', '杜甫', '李白', '王之涣', '', 'B', '此句出自唐代诗人李白的《望庐山瀑布》。');
INSERT INTO "zhishione" VALUES (6, '“千山鸟飞绝，万径人踪灭。”——《江雪》,出自哪位诗人的作品？', '王维', '柳宗元', '孟浩然', '', 'B', '此句出自唐代诗人柳宗元的《江雪》。');
INSERT INTO "zhishione" VALUES (7, '“夜来风雨声，花落知多少。”——《春晓》,出自哪位诗人的作品？', '孟浩然', '李白', '杜甫', '', 'A', '此句出自唐代诗人孟浩然的《春晓》。');
INSERT INTO "zhishione" VALUES (8, '“慈母手中线，游子身上衣。”——《游子吟》,出自哪位诗人的作品？', '李白', '孟郊', '杜甫', '', 'B', '此句出自唐代诗人孟郊的《游子吟》。');
INSERT INTO "zhishione" VALUES (9, '“朝辞白帝彩云间，千里江陵一日还。”——《早发白帝城》,出自哪位诗人的作品？', '王维', '杜甫', '李白', '', 'C', '此句出自唐代诗人李白的《早发白帝城》。');
INSERT INTO "zhishione" VALUES (10, '“清明时节雨纷纷，路上行人欲断魂。”——《清明》,出自哪位诗人的作品？', '杜牧', '李白', '王维', '', 'A', '此句出自唐代诗人杜牧的《清明》,表达了清明时节细雨纷纷，行人心情沉重的景象。');
INSERT INTO "zhishione" VALUES (11, '“烟笼寒水月笼沙，夜泊秦淮近酒家。”——《泊秦淮》,出自哪位诗人的作品？', '王昌龄', '李白', '杜牧', '', 'C', '此句出自唐代诗人杜牧的《泊秦淮》。');
INSERT INTO "zhishione" VALUES (12, '“银烛秋光冷画屏，轻罗小扇扑流萤。”——《秋夕》,出自哪位诗人的作品？', '杜牧', '李商隐', '王维', '', 'A', '此句出自唐代诗人杜牧的《秋夕》。');
INSERT INTO "zhishione" VALUES (13, '“向晚意不适，驱车登古原。”——《乐游原》,出自哪位诗人的作品？', '李白', '杜牧', '李商隐', '', 'C', '此句出自唐代诗人李商隐的《乐游原》。');
INSERT INTO "zhishione" VALUES (14, '“独在异乡为异客，每逢佳节倍思亲。”——《九月九日忆山东兄弟》,出自哪位诗人的作品？', '李白', '王维', '杜甫', '', 'B', '此句出自唐代诗人王维的《九月九日忆山东兄弟》。');
INSERT INTO "zhishione" VALUES (15, '“故人西辞黄鹤楼，烟花三月下扬州。”——《黄鹤楼送孟浩然之广陵》,出自哪位诗人的作品？', '杜甫', '孟浩然', '李白', '', 'C', '此句出自唐代诗人李白的《黄鹤楼送孟浩然之广陵》。');
INSERT INTO "zhishione" VALUES (16, '“空山不见人，但闻人语响。”——《鹿柴》,出自哪位诗人的作品？', '王维', '李白', '杜甫', '', 'A', '此句出自唐代诗人王维的《鹿柴》。');
INSERT INTO "zhishione" VALUES (17, '“明月松间照，清泉石上流。”——《山居秋暝》（王维）,出自哪位诗人的作品？', '李白', '王维', '孟浩然', '', 'B', '此句出自唐代诗人王维的《山居秋暝》。');
INSERT INTO "zhishione" VALUES (18, '“劝君更尽一杯酒，西出阳关无故人。”——《渭城曲》,出自哪位诗人的作品？', '王维', '李白', '杜牧', '', 'A', '此句出自唐代诗人王维的《渭城曲》。');
INSERT INTO "zhishione" VALUES (19, '“春种一粒粟，秋收万颗子。”——《悯农》（其一）,出自哪位诗人的作品？', '李绅', '李白', '杜甫', '', 'A', '此句出自唐代诗人李绅的《悯农》（其一）。');
INSERT INTO "zhishione" VALUES (20, '“锄禾日当午，汗滴禾下土。”——《悯农》（其二）,出自哪位诗人的作品？', '王维', '孟浩然', '李绅', '', 'C', '此句出自唐代诗人李绅的《悯农》（其二）。');
INSERT INTO "zhishione" VALUES (21, '“小时不识月，呼作白玉盘。”——《古朗月行》', '王维', '杜甫', '李白', '', 'C', '此句出自唐代诗人李白的《古朗月行》,描绘了诗人儿时对月亮的纯真想象。');
INSERT INTO "zhishione" VALUES (22, '“远上寒山石径斜，白云深处有人家。”——《山行》', '杜牧', '李白', '王维', '', 'A', '此句出自唐代诗人杜牧的《山行》。');
INSERT INTO "zhishione" VALUES (23, '“停车坐爱枫林晚，霜叶红于二月花。”——《山行》', '李白', '杜牧', '王维', '', 'B', '此句同样出自唐代诗人杜牧的《山行》。');
INSERT INTO "zhishione" VALUES (24, '“孤帆远影碧空尽，唯见长江天际流。”——《黄鹤楼送孟浩然之广陵》', '杜甫', '孟浩然', '李白', '', 'C', '此句出自唐代诗人李白的《黄鹤楼送孟浩然之广陵》。');
INSERT INTO "zhishione" VALUES (25, '“两个黄鹂鸣翠柳，一行白鹭上青天。”——《绝句》', '杜甫', '李白', '王维', '', 'A', '此句出自唐代诗人杜甫的《绝句》。');
INSERT INTO "zhishione" VALUES (26, '“窗含西岭千秋雪，门泊东吴万里船。”——《绝句》', '李白', '杜甫', '孟浩然', '', 'B', '此句同样出自唐代诗人杜甫的《绝句》。');
INSERT INTO "zhishione" VALUES (27, '“牧童骑黄牛，歌声振林樾。”——《所见》', '袁枚', '李白', '杜甫', '', 'A', '此句出自清代诗人袁枚的《所见》。');
INSERT INTO "zhishione" VALUES (28, '“意欲捕鸣蝉，忽然闭口立。”——《所见》', '李白', '袁枚', '孟浩然', '', 'B', '此句同样出自清代诗人袁枚的《所见》。');
INSERT INTO "zhishione" VALUES (29, '“小娃撑小艇，偷采白莲回。”——《池上》', '李白', '白居易', '杜甫', '', 'B', '此句出自唐代诗人白居易的《池上》。');
INSERT INTO "zhishione" VALUES (30, '“不解藏踪迹，浮萍一道开。”——《池上》', '白居易', '李白', '王维', '', 'A', '此句同样出自唐代诗人白居易的《池上》。');
INSERT INTO "zhishione" VALUES (31, '“草长莺飞二月天，拂堤杨柳醉春烟。”——《村居》', '高鼎', '李白', '杜甫', '', 'A', '此句出自清代诗人高鼎的《村居》。');
INSERT INTO "zhishione" VALUES (32, '“儿童散学归来早，忙趁东风放纸鸢。”——《村居》', '孟浩然', '李白', '高鼎', '', 'C', '此句同样出自清代诗人高鼎的《村居》。');
INSERT INTO "zhishione" VALUES (33, '“蓬头稚子学垂纶，侧坐莓苔草映身。”——《小儿垂钓》', '胡令能', '李白', '杜甫', '', 'A', '此句出自唐代诗人胡令能的《小儿垂钓》。');
INSERT INTO "zhishione" VALUES (34, '“路人借问遥招手，怕得鱼惊不应人。”——《小儿垂钓》', '胡令能', '李白', '王维', '', 'A', '此句同样出自唐代诗人胡令能的《小儿垂钓》。');
INSERT INTO "zhishione" VALUES (35, '“江上往来人，但爱鲈鱼美。”——《江上渔者》', '杜甫', '李白', '范仲淹', '', 'C', '此句出自宋代诗人范仲淹的《江上渔者》。');
INSERT INTO "zhishione" VALUES (36, '“君看一叶舟，出没风波里。”——《江上渔者》', '范仲淹', '李白', '孟浩然', '', 'A', '此句同样出自宋代诗人范仲淹的《江上渔者》。');
INSERT INTO "zhishione" VALUES (37, '“黑云翻墨未遮山，白雨跳珠乱入船。”——《六月二十七日望湖楼醉书》', '杜甫', '李白', '苏轼', '', 'C', '此句出自宋代诗人苏轼的《六月二十七日望湖楼醉书》。');
INSERT INTO "zhishione" VALUES (38, '“卷地风来忽吹散，望湖楼下水如天。”——《六月二十七日望湖楼醉书》', '苏轼', '李白', '王维', '', 'A', '此句同样出自宋代诗人苏轼的《六月二十七日望湖楼醉书》。');
INSERT INTO "zhishione" VALUES (39, '“毕竟西湖六月中，风光不与四时同。”——《晓出净慈寺送林子方》', '杨万里', '李白', '杜甫', '', 'A', '此句出自宋代诗人杨万里的《晓出净慈寺送林子方》。');
INSERT INTO "zhishione" VALUES (40, '“接天莲叶无穷碧，映日荷花别样红。”——《晓出净慈寺送林子方》', '孟浩然', '李白', '杨万里', '', 'C', '此句同样出自宋代诗人杨万里的《晓出净慈寺送林子方》。');
INSERT INTO "zhishione" VALUES (41, '“泉眼无声惜细流，树阴照水爱晴柔。”——《小池》', '李白', '杨万里', '杜甫', '', 'B', '此句出自宋代诗人杨万里的《小池》。');
INSERT INTO "zhishione" VALUES (42, '“小荷才露尖尖角，早有蜻蜓立上头。”——《小池》', '李白', '杨万里', '王维', '', 'B', '此句同样出自宋代诗人杨万里的《小池》。');
INSERT INTO "zhishione" VALUES (43, '“迟日江山丽，春风花草香。”——《绝句》', '孟浩然', '李白', '杜甫', '', 'C', '此句出自唐代诗人杜甫的《绝句》。');
INSERT INTO "zhishione" VALUES (44, '“泥融飞燕子，沙暖睡鸳鸯。”——《绝句》', '杜甫', '李白', '王维', '', 'A', '此句同样出自唐代诗人杜甫的《绝句》。');
INSERT INTO "zhishione" VALUES (45, '“采得百花成蜜后，为谁辛苦为谁甜。”——《蜂》', '罗隐', '李白', '杜甫', '', 'A', '此句出自唐代诗人罗隐的《蜂》。');
INSERT INTO "zhishione" VALUES (46, '“天街小雨润如酥，草色遥看近却无。”——《早春呈水部张十八员外》', '王维', '李白', '韩愈', '', 'C', '此句出自唐代诗人韩愈的《早春呈水部张十八员外》。');
INSERT INTO "zhishione" VALUES (47, '“最是一年春好处，绝胜烟柳满皇都。”——《早春呈水部张十八员外》', '孟浩然', '李白', '韩愈', '', 'C', '此句同样出自唐代诗人韩愈的《早春呈水部张十八员外》。');
INSERT INTO "zhishione" VALUES (48, '“独怜幽草涧边生，上有黄鹂深树鸣。”——《滁州西涧》', '韦应物', '李白', '杜甫', '', 'A', '此句出自唐代诗人韦应物的《滁州西涧》。描绘了山涧水边的幽静景象。');
INSERT INTO "zhishione" VALUES (49, '“春潮带雨晚来急，野渡无人舟自横。”——《滁州西涧》', '韦应物', '李白', '王维', '', 'A', '此句同样出自唐代诗人韦应物的《滁州西涧》。描绘了山涧水边的幽静景象。');
INSERT INTO "zhishione" VALUES (50, '“月黑雁飞高，单于夜遁逃。”——《塞下曲》', '李白', '卢纶', '杜甫', '', 'B', '此句出自唐代诗人卢纶的《塞下曲》。');
INSERT INTO "zhishione" VALUES (51, '“欲将轻骑逐，大雪满弓刀。”——《塞下曲》', '孟浩然', '李白', '卢纶', '', 'C', '此句同样出自唐代诗人卢纶的《塞下曲》。');
INSERT INTO "zhishione" VALUES (52, '“平明寻白羽，没在石棱中。”——《塞下曲》', '李白', '卢纶', '王维', '', 'B', '此句也出自唐代诗人卢纶的《塞下曲》系列。');
INSERT INTO "zhishione" VALUES (53, '“湖光秋月两相和，潭面无风镜未磨。”——《望洞庭》', '杜甫', '李白', '刘禹锡', '', 'C', '此句出自唐代诗人刘禹锡的《望洞庭》。');
INSERT INTO "zhishione" VALUES (54, '“遥望洞庭山水翠，白银盘里一青螺。”——《望洞庭》', '王维', '李白', '刘禹锡', '', 'C', '此句同样出自唐代诗人刘禹锡的《望洞庭》。');
INSERT INTO "zhishione" VALUES (55, '“九曲黄河万里沙，浪淘风簸自天涯。”——《浪淘沙》', '刘禹锡', '李白', '杜甫', '', 'A', '此句出自唐代诗人刘禹锡的《浪淘沙》。');
INSERT INTO "zhishione" VALUES (56, '“如今直上银河去，同到牵牛织女家。”——《浪淘沙》', '孟浩然', '李白', '刘禹锡', '', 'C', '此句同样出自唐代诗人刘禹锡的《浪淘沙》。');
INSERT INTO "zhishione" VALUES (57, '“青山隐隐水迢迢，秋尽江南草未凋。”——《寄扬州韩绰判官》', '杜牧', '李白', '王维', '', 'A', '此句出自唐代诗人杜牧的《寄扬州韩绰判官》。');
INSERT INTO "zhishione" VALUES (58, '“二十四桥明月夜，玉人何处教吹箫。”——《寄扬州韩绰判官》,出自哪位诗人的作品？', '李白', '杜牧', '孟浩然', '', 'B', '这句诗出自杜牧的《寄扬州韩绰判官》，以婉约笔触描绘江南秋景,并含蓄表达对友人的思念。');
INSERT INTO "zhishione" VALUES (59, '“野径云俱黑，江船火独明。”——《春夜喜雨》,出自哪位诗人的作品？', '杜甫', '李白', '王维', '', 'A', '这句诗出自杜甫的《春夜喜雨》，描写了春夜雨中的景象。');
INSERT INTO "zhishione" VALUES (60, '“晓看红湿处，花重锦官城。”——《春夜喜雨》,出自哪位诗人的作品？', '李白', '杜甫', '孟浩然', '', 'B', '这句诗出自杜甫的《春夜喜雨》，描写了雨后花朵的美丽。');
INSERT INTO "zhishione" VALUES (61, '“西塞山前白鹭飞，桃花流水鳜鱼肥。”——《渔歌子》,出自哪位诗人的作品？', '杜甫', '李白', '张志和', '', 'C', '这句诗出自张志和的《渔歌子》，描写了西塞山前的自然景色。');
INSERT INTO "zhishione" VALUES (62, '“青箬笠，绿蓑衣，斜风细雨不须归。”——《渔歌子》,出自哪位诗人的作品？', '张志和', '李白', '王维', '', 'A', '这句诗出自张志和的《渔歌子》，描写了渔夫在斜风细雨中的生活。');
INSERT INTO "zhishione" VALUES (63, '“松下问童子，言师采药去。”——《寻隐者不遇》,出自哪位诗人的作品？', '李白', '贾岛', '杜甫', '', 'B', '这句诗出自贾岛的《寻隐者不遇》，描写了诗人寻找隐者未果的情景。');
INSERT INTO "zhishione" VALUES (64, '“只在此山中，云深不知处。”——《寻隐者不遇》,出自哪位诗人的作品？', '李白', '贾岛', '孟浩然', '', 'B', '这句诗出自贾岛的《寻隐者不遇》，描写了隐者所在的山中云雾缭绕。');
INSERT INTO "zhishione" VALUES (65, '“少小离家老大回，乡音无改鬓毛衰。”——《回乡偶书》,出自哪位诗人的作品？', '杜甫', '李白', '贺知章', '', 'C', '这句诗出自贺知章的《回乡偶书》，描写了诗人多年后回乡的感受。');
INSERT INTO "zhishione" VALUES (66, '“儿童相见不相识，笑问客从何处来。”——《回乡偶书》,出自哪位诗人的作品？', '贺知章', '李白', '王维', '', 'A', '这句诗出自贺知章的《回乡偶书》，描写了诗人回乡后儿童不认识他的情景。');
INSERT INTO "zhishione" VALUES (67, '“洛阳亲友如相问，一片冰心在玉壶。”——《芙蓉楼送辛渐》,出自哪位诗人的作品？', '杜甫', '李白', '王昌龄', '', 'C', '这句诗出自王昌龄的《芙蓉楼送辛渐》，表达了诗人高洁的情操。');
INSERT INTO "zhishione" VALUES (68, '“寒雨连江夜入吴，平明送客楚山孤。”——《芙蓉楼送辛渐》,出自哪位诗人的作品？', '王昌龄', '李白', '孟浩然', '', 'A', '这句诗出自王昌龄的《芙蓉楼送辛渐》，描写了送别时的凄凉景象。');
INSERT INTO "zhishione" VALUES (69, '“黄河远上白云间，一片孤城万仞山。”——《凉州词》,出自哪位诗人的作品？', '王维', '李白', '王之涣', '', 'C', '这句诗出自王之涣的《凉州词》，描写了边塞的壮丽景色。');
INSERT INTO "zhishione" VALUES (70, '“故人具鸡黍，邀我至田家。”——《过故人庄》,出自哪位诗人的作品？', '李白', '孟浩然', '杜甫', '', 'B', '这句诗出自孟浩然的《过故人庄》，描写了诗人到朋友家做客的情景。');
INSERT INTO "zhishione" VALUES (71, '“绿树村边合，青山郭外斜。”——《过故人庄》,出自哪位诗人的作品？', '王维', '李白', '孟浩然', '', 'C', '这句诗出自孟浩然的《过故人庄》，描写了村庄周围的自然景色。');
INSERT INTO "zhishione" VALUES (72, '“开轩面场圃，把酒话桑麻。”——《过故人庄》,出自哪位诗人的作品？', '孟浩然', '李白', '孟浩然', '', 'A', '这句诗出自孟浩然的《过故人庄》，描写了诗人与朋友饮酒聊天的情景。');
INSERT INTO "zhishione" VALUES (73, '《静夜思》是谁的作品？', '李白', '王之涣', '杜牧', '白居易', 'A', '《静夜思》是李白的作品，表达了诗人对故乡的思念。');
INSERT INTO "zhishione" VALUES (74, '《床前明月光》的“床前明月光”指的是哪个天体？', '星星', '太阳', '月亮', '云彩', 'C', '《床前明月光》出自李白的《静夜思》，“床前明月光”指的是月亮。');
INSERT INTO "zhishione" VALUES (75, '《春晓》中的“春眠不觉晓”描述的是哪个季节？', '春天', '夏天', '秋天', '冬天', 'A', '《春晓》是孟浩然的作品，“春眠不觉晓”描述的是春天的早晨。');
INSERT INTO "zhishione" VALUES (76, '《咏鹅》这首诗的作者是谁？', '骆宾王', '王之涣', '李白', '白居易', 'A', '《咏鹅》是骆宾王的作品，描写了鹅的形态和动作。');
INSERT INTO "zhishione" VALUES (77, '《鹿柴》是谁的作品？', '王之涣', '王维', '李白', '杜牧', 'B', '《鹿柴》是王维的作品，描写了山中的幽静景色。');
INSERT INTO "zhishione" VALUES (78, '《早发白帝城》讲述的是作者的什么心情？', '快乐', '伤感', '惊讶', '急切', 'D', '《早发白帝城》是李白的作品，表达了诗人急切的心情。');
INSERT INTO "zhishione" VALUES (79, '《悯农》这首诗写的是什么内容？', '劳动', '爱情', '友情', '自然', 'A', '《悯农》是李绅的作品，描写了农民的辛勤劳动。');
INSERT INTO "zhishione" VALUES (80, '《登鹳雀楼》的诗意是什么？', '描写自然景色', '表达友情', '赞美家乡', '描述山川', 'A', '《登鹳雀楼》是王之涣的作品，描写了登楼远眺的自然景色。');
INSERT INTO "zhishione" VALUES (81, '《春望》这首诗的作者是谁？', '杜甫', '李白', '王之涣', '白居易', 'A', '《春望》是杜甫的作品，描写了战乱中的春天景象。');
INSERT INTO "zhishione" VALUES (82, '《夜泊牛渚怀古》描述的是哪个地点？', '长江', '黄山', '牛渚', '苏州', 'C', '《夜泊牛渚怀古》是李白的作品，描写了牛渚的夜景。');
INSERT INTO "zhishione" VALUES (83, '“桃花潭水深千尺，不及汪伦送我情”出自哪首诗？', '《赠汪伦》', '《静夜思》', '《早发白帝城》', '《春晓》', 'A', '这句诗出自李白的《赠汪伦》，表达了诗人对朋友汪伦的深厚情谊。以桃花潭水比喻友情之深，表达了诗人对朋友汪伦的深厚感激之情。');
INSERT INTO "zhishione" VALUES (84, '“竹外桃花三两枝，春江水暖鸭先知”出自哪首诗？', '《春江花月夜》', '《渔舟唱晚》', '《春望》', '《惠崇春江晚景》', 'D', '这句诗出自苏轼的《惠崇春江晚景》。描绘了早春江景的生机勃勃。');
INSERT INTO "zhishione" VALUES (85, '《清平调》这首诗的作者是谁？', '李白', '白居易', '杜甫', '王维', 'A', '《清平调》是李白的作品，描写了杨贵妃的美貌。');
INSERT INTO "zhishione" VALUES (86, '《早发白帝城》中“朝辞白帝彩云间”讲述的是作者的什么情感？', '喜悦', '怀念', '不舍', '惊讶', 'A', '这句诗出自李白的《早发白帝城》，表达了诗人心情愉悦。');
INSERT INTO "zhishione" VALUES (87, '《望庐山瀑布》中的“日照香炉生紫烟”写的是哪种自然景象？', '太阳照耀山顶香炉上的烟雾', '瀑布飞流', '夜晚的月光', '-', 'A', '这句诗出自李白的《望庐山瀑布》，描写了太阳照耀庐山香炉峰时的景象。');
INSERT INTO "zhishione" VALUES (88, '《春江花月夜》作者是谁？', '白居易', '王维', '杜甫', '张若虚', 'D', '《春江花月夜》是张若虚的作品，描写了春江月夜的美丽景色。');
INSERT INTO "zhishione" VALUES (89, '“人有悲欢离合，月有阴晴圆缺”出自哪首诗？', '《水调歌头》', '《静夜思》', '《长恨歌》', '《登高》', 'A', '这句诗出自苏轼的《水调歌头》，表达了人生的悲欢离合和月亮的阴晴圆缺。');
INSERT INTO "zhishione" VALUES (90, '《登鹳雀楼》中的“白日依山尽”描述的是哪个景象？', '白天即将结束', '夜晚来临', '太阳升起', '河水涨满', 'A', '这句诗出自王之涣的《登鹳雀楼》，描写了太阳落山的景象。');
INSERT INTO "zhishione" VALUES (91, '《静夜思》的主题是什么？', '对月的思念', '春天的美丽', '自然景色的描写', '战争的悲伤', 'A', '《静夜思》是李白的作品，表达了诗人对故乡的思念。');
INSERT INTO "zhishione" VALUES (92, '《望庐山瀑布》写的是什么景象？', '瀑布飞流直下', '春天的桃花', '夏日的江水', '秋天的枯树', 'A', '《望庐山瀑布》是李白的作品，描写了庐山瀑布的壮丽景象。');
INSERT INTO "zhishione" VALUES (93, '“劝君更尽一杯酒，西出阳关无故人”出自哪首诗？', '《送别》', '《早发白帝城》', '《夜泊牛渚怀古》', '《送杜少府之任蜀州》', 'A', '这句诗出自王维的《送元二使安西》，表达了诗人对友人的不舍之情。');
INSERT INTO "zhishione" VALUES (94, '《清明》这首诗表达了什么情感？', '伤感', '快乐', '希望', '怀念', 'D', '《清明》是杜牧的作品，表达了诗人对逝去亲人的怀念之情。');
INSERT INTO "zhishione" VALUES (95, '《赋得古原草送别》的作者是谁？', '白居易', '王之涣', '李白', '骆宾王', 'A', '《赋得古原草送别》是白居易的作品，全诗通过描绘古原野草的顽强生命力与送别之情,表达了对自然规律的深刻感悟和离愁别绪的细腻刻画。');
INSERT INTO "zhishione" VALUES (96, '“床前明月光，疑是地上霜”描写的是什么？', '月光', '霜', '雨水', '早晨的阳光', 'A', '这句诗出自李白的《静夜思》，描写了月光照在床前的情景。');
INSERT INTO "zhishione" VALUES (97, '《夜泊牛渚怀古》描述的是谁的故居？', '牛渚（指地点）', '杜甫', '李白', '王之涣', 'A', '《夜泊牛渚怀古》是李白的作品，描写了牛渚的夜景。');
INSERT INTO "zhishione" VALUES (98, '《春晓》这首诗的作者是谁？', '孟浩然', '王维', '白居易', '杜牧', 'A', '《春晓》是孟浩然的作品，描写了春天的早晨。');
INSERT INTO "zhishione" VALUES (99, '“故人西辞黄鹤楼，烟花三月下扬州”出自哪首诗？', '《送别》', '《夜泊牛渚怀古》', '《黄鹤楼》', '《早发白帝城》', 'C', '这句诗出自李白的《黄鹤楼送孟浩然之广陵》，描写了送别的情景。');
INSERT INTO "zhishione" VALUES (100, '《静夜思》中“举头望明月，低头思故乡”表达了作者什么情感？', '思乡', '快乐', '迷茫', '惆怅', 'A', '这句诗出自李白的《静夜思》，表达了诗人对故乡的思念。');
INSERT INTO "zhishione" VALUES (101, '“野火烧不尽，春风吹又生”出自哪首诗？', '《春望》', '《咏鹅》', '《悯农》', '《赋得古原草送别》', 'D', '这句诗出自白居易的《赋得古原草送别》，描写了草的顽强生命力。');
INSERT INTO "zhishione" VALUES (102, '《春江花月夜》的作者是谁？', '白居易', '王维', '李白', '张若虚', 'D', '《春江花月夜》是张若虚的作品，描写了春江月夜的美丽景色。');
INSERT INTO "zhishione" VALUES (103, '“白日依山尽，黄河入海流”表达的是什么景象？', '大自然的广阔', '日出', '春天的生机', '黄河的浩渺', 'A', '这句诗出自王之涣的《登鹳雀楼》，描写了大自然的广阔景象。');
INSERT INTO "zhishione" VALUES (104, '“天高地迥，觉宇宙之无穷”出自哪首诗？', '《登高》', '《滕王阁序》', '《长恨歌》', '《送别》', 'B', '这句诗出自王勃的《滕王阁序》,描绘了登高望远所见的壮阔景象。');
INSERT INTO "zhishione" VALUES (105, '《望庐山瀑布》诗中“飞流直下三千尺”形容了什么景象？', '瀑布', '山川', '江水', '高楼', 'A', '这句诗出自李白的《望庐山瀑布》，描写了庐山瀑布的壮丽景象。');
INSERT INTO "zhishione" VALUES (106, '《春望》表达了诗人什么样的情感？', '伤感', '忧国忧民', '欢快', '思乡', 'B', '《春望》是杜甫的作品，表达了诗人对国家和人民的忧虑。');
INSERT INTO "zhishione" VALUES (107, '“天阶夜色凉如水，卧看牵牛织女星”出自哪首诗？', '《夜泊牛渚怀古》', '《静夜思》', '《七夕》', '《长恨歌》', 'C', '这句诗出自杜牧的《七夕》，描写了七夕夜晚的景色。');
INSERT INTO "zhishione" VALUES (108, '《夜泊牛渚怀古》中的“想见”指的是谁？', '李白', '杜甫', '王之涣', '张若虚', 'A', '《夜泊牛渚怀古》是李白的作品，表达了诗人对古人的怀念。');
INSERT INTO "zhishione" VALUES (109, '《静夜思》中的“月光”象征了什么？', '光明', '思念', '快乐', '休息', 'B', '《静夜思》是李白的作品，月光象征着诗人对故乡的思念。');
INSERT INTO "zhishione" VALUES (110, '《望庐山瀑布》中的“飞流直下三千尺”表达了什么情感？', '震撼', '平静', '快乐', '哀愁', 'A', '这句诗出自李白的《望庐山瀑布》，表达了诗人对瀑布壮丽景象的震撼。');
INSERT INTO "zhishione" VALUES (111, '“无边落木萧萧下”出自哪首诗？', '《登高》', '《夜泊牛渚怀古》', '《春晓》', '《长恨歌》', 'A', '这句诗出自杜甫的《登高》，描写了秋天落叶的景象。');
INSERT INTO "zhishione" VALUES (112, '《咏鹅》中的“白毛浮绿水”写的是哪种动物？', '鹅', '鸟', '鱼', '虾', 'A', '出自骆宾王的《咏鹅》，描写鹅在水中的情景。');
INSERT INTO "zhishione" VALUES (113, '《早发白帝城》中“白帝城”位于哪个城市？', '重庆奉节', '成都', '武汉', '长安', 'A', '白帝城位于今重庆市奉节县，是古代军事要塞。');
INSERT INTO "zhishione" VALUES (114, '“白日依山尽，黄河入海流”表达的是什么？', '太阳落山，黄河入海', '诗人怀念故乡', '自然界的广阔', '黄河的浩渺', 'A', '出自王之涣《登鹳雀楼》，描写自然壮丽景象。');
INSERT INTO "zhishione" VALUES (115, '《悯农》的作者是谁？', '李白', '王之涣', '李绅', '杜牧', 'C', '《悯农》是李绅的作品，描写农民辛勤劳动。');
INSERT INTO "zhishione" VALUES (116, '《悯农》讲述的是哪种人群的辛苦？', '学生', '农民', '工人', '商人', 'B', '描写农民辛勤劳动和生活艰辛。');
INSERT INTO "zhishione" VALUES (117, '《春望》中“国破山河在”表达什么？', '战乱破败，但山河依旧的景象', '春天的生机', '自然景色', '忧国忧民', 'D', '杜甫《春望》表达诗人在战乱时期对国家和人民的深切关怀以及对战乱带来的破坏的悲痛之情。');
INSERT INTO "zhishione" VALUES (118, '《早发白帝城》中“朝辞白帝彩云间”的情感？', '喜悦', '伤感', '不舍', '惆怅', 'A', '李白此诗表达遇赦后的轻松愉快心情。');
INSERT INTO "zhishione" VALUES (119, '《静夜思》中月光给人的感觉？', '宁静', '欢快', '伤感', '激动', 'A', '李白作品，营造宁静思乡氛围。');
INSERT INTO "zhishione" VALUES (120, '《赠汪伦》中“桃花潭水深千尺”表达？', '友谊深厚', '爱情忠诚', '乡土记忆', '自然敬仰', 'A', '表达对好友汪伦的友情。');
INSERT INTO "zhishione" VALUES (121, '“白日依山尽”写的是哪个景象？', '自然壮丽', '太阳落山', '长河入海', '山水相依', 'A', '王之涣《登鹳雀楼》的经典画面。');
INSERT INTO "zhishione" VALUES (122, '《春晓》中“春眠不觉晓”指哪种情形？', '春天美好', '春天懒散', '春天忙碌', '春天喧闹', 'B', '孟浩然描写春日慵懒的晨间感受。');
INSERT INTO "zhishione" VALUES (123, '“无边落木萧萧下”表达什么？', '自然壮丽', '秋天萧瑟', '季节变化', '心境荒凉', 'B', '杜甫《登高》渲染秋日萧瑟氛围。');
INSERT INTO "zhishione" VALUES (124, '《江雪》的作者是谁？', '王之涣', '刘禹锡', '白居易', '柳宗元', 'D', '柳宗元借江雪孤舟表达孤傲心境。');
INSERT INTO "zhishione" VALUES (125, '“飞流直下三千尺”描写什么？', '夕阳', '瀑布', '雷雨', '江水', 'B', '李白《望庐山瀑布》的夸张描写。');
INSERT INTO "zhishione" VALUES (126, '《咏鹅》中“红掌拨清波”的动物？', '鸡', '鹅', '鸭', '兔', 'B', '骆宾王七岁时创作的咏物诗经典。');
INSERT INTO "zhishione" VALUES (127, '“朱雀桥边野草花”出自哪首诗？', '《乌衣巷》', '《滕王阁序》', '《早发白帝城》', '《长恨歌》', 'A', '刘禹锡《乌衣巷》的怀古名句。');
INSERT INTO "zhishione" VALUES (128, '《悯农》中“锄禾日当午”描写？', '农田劳作', '城市劳动', '学校学习', '工厂生产', 'A', '李绅展现农民田间劳作的艰辛。');
INSERT INTO "zhishione" VALUES (129, '“月黑雁飞高”出自哪首诗？', '《江雪》', '《塞下曲》', '《夜泊牛渚》', '《凉州词》', 'B', '卢纶《塞下曲》描写边塞战事。');
INSERT INTO "zhishione" VALUES (130, '“黄河远上白云间”出自？', '《送别》', '《凉州词》', '《出塞》', '《送杜少府》', 'B', '王之涣《凉州词》的边塞壮景描写。');
INSERT INTO "zhishione" VALUES (131, '《夜泊牛渚怀古》写哪位人物？', '谢尚', '李白', '王勃', '刘备', 'A', '李白追忆东晋谢尚的知遇典故。');
INSERT INTO "zhishione" VALUES (132, '《赋得古原草送别》描写季节？', '春天', '夏天', '秋天', '冬天', 'A', '白居易"野火烧不尽"的春日景象。');
INSERT INTO "zhishione" VALUES (133, '《春望》中“国破山河在”指什么？', '战乱破败', '和平安定', '自然景象', '诗人感慨', 'A', '杜甫目睹安史之乱后的都城惨状。');
