INSERT INTO "zhishione" VALUES (277, '“试问卷帘人，却道海棠依旧”出自哪首词？', '《滕王阁序》', '《春望》', '《如梦令》', '《浪淘沙》', 'C', '出自李清照《如梦令》。');
INSERT INTO "zhishione" VALUES (278, '“山中相送罢”出自哪首诗？', '《黄鹤楼》', '《江南春》', '《山中送别》', '《滕王阁序》', 'C', '出自王维《山中送别》。');
INSERT INTO "zhishione" VALUES (279, '“一去二三里，烟村四五家”出自哪首诗？', '《早发白帝城》', '《江南春》', '《夜泊牛渚怀古》', '《山村咏怀》', 'D', '出自邵雍《山村咏怀》');
INSERT INTO "zhishione" VALUES (280, '“树树皆秋色，山山唯落晖”出自哪首诗？', '《野望》', '《江南春》', '《望庐山瀑布》', '《水调歌头》', 'A', '出自王绩《野望》。');
INSERT INTO "zhishione" VALUES (281, '“山中无历日，寒尽不知春”出自哪首诗？', '《江雪》', '《鹿柴》', '《答人》', '《登高》', 'C', '出自太上隐者《答人》');
INSERT INTO "zhishione" VALUES (282, '“天街小雨润如酥，草色遥看近却无”出自哪首诗？', '《早春呈水部张十八员外》', '《登高》', '《夜泊牛渚怀古》', '《望庐山瀑布》', 'A', '出自唐代韩愈。');
INSERT INTO "zhishione" VALUES (283, '“浮光跃金，静影沉璧”出自哪篇文章？', '《庐山谣》', '《滕王阁序》', '《岳阳楼记》', '《黄鹤楼》', 'C', '出自范仲淹《岳阳楼记》。');
INSERT INTO "zhishione" VALUES (284, '“不知细叶谁裁出，二月春风似剪刀”出自哪首诗？', '《夜泊牛渚怀古》', '《春望》', '《借问酒家何处有》', '《咏柳》', 'D', '出自唐代贺知章《咏柳》。');
INSERT INTO "zhishione" VALUES (285, '“清明时节雨纷纷，路上行人欲断魂”出自哪首诗？', '《夜泊牛渚怀古》', '《春望》', '《清明》', '《登高》', 'C', '杜牧《清明》。');
INSERT INTO "zhishione" VALUES (286, '“万里长江横渡，翻江倒海”出自哪首词？', '《庐山谣》', '《水调歌头》', '《黄鹤楼》', '《水调歌头·游泳》', 'D', '出自毛泽东《水调歌头·游泳》。');
INSERT INTO "zhishione" VALUES (287, '“年年岁岁花相似，岁岁年年人不同”出自哪首诗？', '《江南春》', '《静夜思》', '《代悲白头翁》', '《登高》', 'C', '出自唐代刘希夷《代悲白头翁》。');
INSERT INTO "zhishione" VALUES (288, '“水何澹澹，山岛竦峙”出自哪首诗？', '《观沧海》', '《庐山谣》', '《春望》', '《静夜思》', 'A', '出自曹操《观沧海》。');
INSERT INTO "zhishione" VALUES (289, '“暮云收尽溢清寒，银汉无声转玉盘”出自哪首词？', '《水调歌头》', '《阳关曲·中秋月》', '《滕王阁序》', '《黄鹤楼》', 'B', '出自苏轼《阳关曲·中秋月》。');
INSERT INTO "zhishione" VALUES (290, '“墙角数枝梅，凌寒独自开”出自哪首诗？', '《早春呈水部张十八员外》', '《庐山谣》', '《滕王阁序》', '《梅花》', 'D', '出自宋代王安石《梅花》。');
INSERT INTO "zhishione" VALUES (291, '“可怜夜半虚前席，不问苍生问鬼神”出自哪首诗？', '《夜泊牛渚怀古》', '《庐山谣》', '《贾生》', '《破阵子》', 'C', '出自唐代李商隐《贾生》。');
INSERT INTO "zhishione" VALUES (292, '“夕阳西下，断肠人在天涯”出自哪首元曲？', '《夜泊牛渚怀古》', '《庐山谣》', '《登高》', '《天净沙·秋思》', 'D', '出自元代马致远《天净沙·秋思》。');
INSERT INTO "zhishione" VALUES (293, '“白云千载空悠悠？”出自哪首诗？', '《庐山谣》', '《长恨歌》', '《黄鹤楼》', '《水调歌头》', 'C', '出自崔颢《黄鹤楼》');
INSERT INTO "zhishione" VALUES (294, '“举杯邀明月，对影成三人”出自哪首诗？', '《月下独酌四首·其一》', '《庐山谣》', '《黄鹤楼》', '《滕王阁序》', 'A', '出自唐代李白《月下独酌四首·其一》，展现了诗人月下独酌的孤独与豪放。');
INSERT INTO "zhishione" VALUES (295, '“人生自古谁无死，留取丹心照汗青”出自哪首诗？', '《黄鹤楼》', '《夜泊牛渚怀古》', '《庐山谣》', '《过零丁洋》', 'D', '出自唐代文天祥《过零丁洋》');
