INSERT INTO "zhishitwo" VALUES (1, '“独怜幽草涧边生，上有黄鹂深树鸣。”出自哪首诗？', '《静夜思》', '《春晓》', '《滁州西涧》', '《赋得古原草送别》', 'C', '出自韦应物《滁州西涧》，描写滁州西涧春景。');
INSERT INTO "zhishitwo" VALUES (2, '“白日放歌须纵酒，青春作伴好还乡。”表达了诗人什么情感？', '欢乐', '悲伤', '思乡', '忧国', 'A', '杜甫听闻安史之乱平定后的狂喜之情。');
INSERT INTO "zhishitwo" VALUES (3, '“野径云俱黑，江船火独明。”的下一句是什么？', '花重锦官城', '晓看红湿处', '夜来风雨声', '花落知多少', 'B', '出自杜甫《春夜喜雨》，描绘雨后花景。');
INSERT INTO "zhishitwo" VALUES (4, '“洛阳亲友如相问，一片冰心在玉壶。”中的“冰心”象征什么？', '纯洁的心灵', '冰冷的石头', '清澈的湖水', '洁白的雪花', 'A', '王昌龄以“冰心”自喻高洁品格。');
INSERT INTO "zhishitwo" VALUES (5, '以下哪些诗句出自《望庐山瀑布》？（多选）', '飞流直下三千尺', '疑是银河落九天', ' 窗含西岭千秋雪', '门泊东吴万里船', 'AB', 'AB为李白《望庐山瀑布》。');
INSERT INTO "zhishitwo" VALUES (6, '“春风拂槛露华浓”描述的是哪种花？', '菊花', '梅花', '牡丹', '荷花', 'C', '李白《清平调》赞杨贵妃如牡丹般华贵。');
INSERT INTO "zhishitwo" VALUES (7, '“海内存知己，天涯若比邻。”表达了诗人怎样的情感？', '思乡之情', '孤独寂寞', '友情深厚', '忧国忧民', 'C', '王勃强调友情超越空间距离。');
INSERT INTO "zhishitwo" VALUES (8, '“黑云压城城欲摧，甲光向日金鳞开。”中的“金鳞”指什么？', '铠甲', '鱼鳞', '黄金', '战士', 'A', '李贺以“金鳞”比喻战士铠甲的反光。');
INSERT INTO "zhishitwo" VALUES (9, '以下哪些诗句与秋天有关？（多选）', ' 秋风萧瑟', '枫叶红于二月花', ' 春眠不觉晓', ' 夏日炎炎似火烧', 'AB', 'A（曹操《观沧海》）B（杜牧《山行》）均描写秋景。');
INSERT INTO "zhishitwo" VALUES (10, '“明月松间照，清泉石上流。”出自哪首诗？', '《江南春》', '《春夜喜雨》', '《望洞庭》', '《山居秋暝》', 'D', '王维《山居秋暝》中的山水田园名句。');
INSERT INTO "zhishitwo" VALUES (11, '“千磨万击还坚劲，任尔东西南北风。”赞美了什么？', '菊花的高洁', '松树的挺拔', '梅花的傲骨', '竹子的坚韧', 'D', '郑板桥借竹喻不屈不挠的精神。');
INSERT INTO "zhishitwo" VALUES (12, '“儿童散学归来早，忙趁东风放纸鸢。”中的“纸鸢”指什么？', '风筝', '纸船', '纸飞机', '气球', 'A', '高鼎《村居》中“纸鸢”即风筝。');
INSERT INTO "zhishitwo" VALUES (13, '以下哪些诗句出自《春日》？（多选）', ' 胜日寻芳泗水滨', '无边光景一时新', '等闲识得东风面', '万紫千红总是春', 'ABCD', '朱熹《春日》全诗四句均列于选项中。');
INSERT INTO "zhishitwo" VALUES (14, '“孤帆远影碧空尽，唯见长江天际流。”中的“天际流”指什么？', '天边流淌的河', '长江流向天边', '天边的云彩', '天边的海洋', 'B', '李白《黄鹤楼送孟浩然》写江水与天际相接。');
INSERT INTO "zhishitwo" VALUES (15, '“柴门闻犬吠，风雪夜归人。”中的“归人”指谁？', '回家的诗人', '过路的行人', '邻居', '家人', 'A', '刘长卿《逢雪宿芙蓉山主人》中诗人自指。');
INSERT INTO "zhishitwo" VALUES (16, '以下哪些诗句与春天有关？（多选）', '春眠不觉晓', '处处闻啼鸟', '秋风萧瑟', '冬雪皑皑', 'AB', 'AB出自孟浩然《春晓》，直接点明春景。');
INSERT INTO "zhishitwo" VALUES (17, '“黄河之水天上来，奔流到海不复回。”中的“海”指什么？', '大海', '黄海', '渤海', '南海', 'C', '黄河最终注入渤海，李白以夸张手法写其奔涌入海。');
INSERT INTO "zhishitwo" VALUES (18, '“故人具鸡黍，邀我至田家。”中的“故人”指谁？', '老朋友', '新邻居', '陌生人', '亲戚', 'A', '孟浩然《过故人庄》，“故人”即旧友，体现田园情谊。');
INSERT INTO "zhishitwo" VALUES (19, '“独在异乡为异客，每逢佳节倍思亲。”中的“佳节”指什么？', '中秋节', '重阳节', '春节', '端午节', 'B', '王维《九月九日忆山东兄弟》，专写重阳节思亲之情。');
INSERT INTO "zhishitwo" VALUES (20, '“白日依山尽，黄河入海流。”描绘了哪两种自然景象？', '山', ' 河', '海', '天', 'AC', '王之涣《登鹳雀楼》，白日落山与黄河入海的壮阔景象。');
INSERT INTO "zhishitwo" VALUES (21, '“野火烧不尽，春风吹又生。”表达了诗人怎样的情感？', '生命的顽强', '对火的恐惧', '对春天的期待', '对生命的无奈', 'A', '白居易《赋得古原草送别》，借野草赞生命力的坚韧不屈。');
INSERT INTO "zhishitwo" VALUES (22, '“清明时节雨纷纷，路上行人欲断魂。”中的“行人”指谁？', '扫墓的人', '诗人自己', '路上的人们', '游客', 'C', '杜牧《清明》，“行人”泛指路上冒雨赶路的清明扫墓者。');
INSERT INTO "zhishitwo" VALUES (23, '“遥知兄弟登高处，遍插茱萸少一人。”中的“一人”指谁？', '诗人自己', '诗人的兄弟', '诗人的朋友', '诗人的亲人', 'A', '王维自述未能与兄弟共度重阳，以“少一人”表遗憾。');
INSERT INTO "zhishitwo" VALUES (24, '以下哪些诗句出自《江南春》？（多选）', '千里莺啼绿映红', '水村山郭酒旗风', '南朝四百八十寺', '多少楼台烟雨中', 'ABCD', '杜牧《江南春》全诗四句均列于选项中，展现江南春景与历史感怀。');
INSERT INTO "zhishitwo" VALUES (25, '“采菊东篱下，悠然见南山。”中的“南山”指什么？', '南方的山', '远处的山峰', '特定的山名', '心中的山', 'B', '陶渊明《饮酒》，实指庐山，象征超脱尘世的隐逸心境。');
INSERT INTO "zhishitwo" VALUES (26, '“爆竹声中一岁除，春风送暖入屠苏。”描述的是什么节日？', '春节', '元宵节', '中秋节', '端午节', 'A', '王安石《元日》，爆竹、屠苏酒均为春节典型意象。');
INSERT INTO "zhishitwo" VALUES (27, '“牧童骑黄牛，歌声振林樾。”中的“林樾”指什么？', '河边', '草地', '山坡', '树林', 'D', '袁枚《所见》，“林樾”即树荫，烘托牧童天真烂漫的形象。');
INSERT INTO "zhishitwo" VALUES (28, '“山外青山楼外楼，西湖歌舞几时休。”中的“西湖”在哪里？', '杭州', '苏州', '南京', '扬州', 'A', '林升《题临安邸》，南宋都城临安（今杭州）的西湖繁华景象。');
INSERT INTO "zhishitwo" VALUES (29, '“劝君更尽一杯酒，西出阳关无故人。”表达了诗人怎样的情感？', '对家乡的思念', '对未来的憧憬', '对友人的不舍', '对旅途的期待', 'C', '王维《送元二使安西》，以酒寄情，抒发送别友人的深情。');
INSERT INTO "zhishitwo" VALUES (30, '“故人西辞黄鹤楼，烟花三月下扬州。”中的“烟花”指什么？', '春天的景色', '烟火', '花朵', '繁华的景象', 'A', '李白《黄鹤楼送孟浩然》，“烟花”形容扬州三月繁花似锦的春景。');
INSERT INTO "zhishitwo" VALUES (31, '“两个黄鹂鸣翠柳，一行白鹭上青天。”描述了哪几种动物？', '黄鹂', '白鹭', '翠柳', '青天', 'AB', '杜甫《绝句》，黄鹂、白鹭为动物，翠柳、青天为景物。');
INSERT INTO "zhishitwo" VALUES (32, '“春风又绿江南岸，明月何时照我还？”中的“我”指谁？', '路人', '诗人的朋友', '诗人的亲人', '诗人自己', 'D', '王安石《泊船瓜洲》，以“我”自指，表达思归之情。');
INSERT INTO "zhishitwo" VALUES (33, '“湖光秋月两相和，潭面无风镜未磨。”描述的是哪个湖的景色？', '洞庭湖', '西湖', '太湖', '鄱阳湖', 'A', '出自刘禹锡《望洞庭》，"潭面"即洞庭湖面，以"镜未磨"形容湖面平静。');
INSERT INTO "zhishitwo" VALUES (34, '“横看成岭侧成峰，远近高低各不同。”告诉我们什么道理？', '自然景观多样', '山峰形状多变', '人生道路曲折', '看问题角度不同', 'D', '苏轼《题西林壁》，强调不同视角得出不同结论的哲理。');
INSERT INTO "zhishitwo" VALUES (35, '“草长莺飞二月天，拂堤杨柳醉春烟。”中的“春烟”指什么？', '春天的雾气', '炊烟', '柳絮', '花香', 'A', '高鼎《村居》，"春烟"指春日水泽草木间蒸腾的雾气。');
INSERT INTO "zhishitwo" VALUES (36, '以下哪些诗句出自《长歌行》？（多选）', '青青园中葵', '朝露待日晞', '阳春布德泽', '万物生光辉', 'ABCD', '汉乐府《长歌行》全诗四句均列于选项中，感叹时光易逝。');
INSERT INTO "zhishitwo" VALUES (37, '“煮豆燃豆萁，豆在釜中泣。”表达了诗人怎样的情感？', '对豆子的怜悯', '对生活的无奈', '兄弟相争的痛苦', '对命运的抗争', 'C', '曹植《七步诗》，以豆萁相煎隐喻兄弟相残的悲愤。');
INSERT INTO "zhishitwo" VALUES (38, '“独坐幽篁里，弹琴复长啸。”中的“幽篁”指什么？', '幽静的竹林', '深山的老林', '寂静的山谷', '幽暗的洞穴', 'A', '王维《竹里馆》，"幽篁"即幽深竹林，烘托隐逸意境。');
INSERT INTO "zhishitwo" VALUES (39, '“春蚕到死丝方尽，蜡炬成灰泪始干。”常用来比喻什么？', '生命的短暂', '无私奉献的精神', '爱情的坚贞', '事业的执着', 'B', '李商隐《无题》，原喻爱情至死不渝，现多用于赞颂奉献精神。');
INSERT INTO "zhishitwo" VALUES (40, '“黑云翻墨未遮山，白雨跳珠乱入船。”中的“白雨”指什么？', '大雨点', '白雪', '浪花', '云雾', 'A', '苏轼《六月二十七日望湖楼醉书》，"白雨"形容夏日急雨的密集雨点。');
INSERT INTO "zhishitwo" VALUES (41, '以下哪些诗句与边塞有关？（多选）', '大漠孤烟直', '长河落日圆', '羌笛何须怨杨柳', '春风不度玉门关', 'ABCD', '均出自唐代边塞诗名句（王维《使至塞上》、王之涣《凉州词》）。');
INSERT INTO "zhishitwo" VALUES (42, '“野旷天低树，江清月近人。”表达了诗人怎样的心境？', '孤独寂寞', '心旷神怡', '思乡之情', '忧国忧民', 'A', '孟浩然《宿建德江》，以空旷江景反衬羁旅孤寂之情。');
INSERT INTO "zhishitwo" VALUES (43, '“故人何不返，春华复应晚。”中的“春华”指什么？', '春天的花朵', '年华', '美好的时光', '春天的景色', 'B', '柳恽《江南曲》，"春华"喻指青春年华，感叹时光流逝。');
INSERT INTO "zhishitwo" VALUES (44, '“黄河远上白云间，一片孤城万仞山。”中的“孤城”给人什么感觉？', '热闹非凡', '雄伟壮观', '孤独凄凉', '宁静祥和', 'C', '王之涣《凉州词》，"孤城"凸显边塞苍凉与戍边将士的孤寂。');
INSERT INTO "zhishitwo" VALUES (45, '“庐山秀出南斗傍，屏风九叠云锦张。”中的“南斗”指什么？', '星宿名', '南方', '山峰名', '地名', 'A', '李白《庐山谣寄卢侍御虚舟》中“南斗”指南斗六星，属中国古代二十八宿之一，位于南方天空。诗中用“南斗傍”形容庐山高耸入云，仿佛与星宿相邻。');
INSERT INTO "zhishitwo" VALUES (46, '以下哪些诗句出自《泊船瓜洲》？（多选）', '京口瓜洲一水间', '钟山只隔数重山', '春风又绿江南岸', '明月何时照我还', 'ABCD', '王安石《泊船瓜洲》全诗四句均列于选项中。');
INSERT INTO "zhishitwo" VALUES (47, '“月上柳梢头，人约黄昏后。”描述的是什么场景？', '月下独酌', '黄昏散步', '柳林赏景', '月夜约会', 'D', '欧阳修《生查子·元夕》，描写元宵节恋人相约的情景。');
INSERT INTO "zhishitwo" VALUES (48, '“绿遍山原白满川，子规声里雨如烟。”中的“子规”是什么鸟？', '黄鹂', '喜鹊', '画眉', '杜鹃鸟', 'D', '“子规”即杜鹃鸟，古诗中常借其啼声渲染哀愁或春意。');
INSERT INTO "zhishitwo" VALUES (49, '“王师北定中原日，家祭无忘告乃翁。”表达了诗人怎样的遗愿？', '收复失地', '报效国家', '安享晚年', '子孙满堂', 'A', '陆游《示儿》，临终仍盼国家统一，体现爱国情怀。');
INSERT INTO "zhishitwo" VALUES (50, '“山重水复疑无路，柳暗花明又一村。”常用来形容什么情境？', '旅途的艰辛', '困境中的希望', '风景的优美', '人生的波折', 'B', '陆游《游山西村》，喻指绝处逢生的转机。');
INSERT INTO "zhishitwo" VALUES (51, '“寒雨连江夜入吴，平明送客楚山孤。”中的“楚山”在哪里？', '浙江境内', '湖北境内', '江苏境内', '长江以南地区', 'D', '王昌龄《芙蓉楼送辛渐》，“楚山”泛指江南吴楚之地。');
INSERT INTO "zhishitwo" VALUES (52, '“青箬笠，绿蓑衣，斜风细雨不须归。”中的“蓑衣”是用什么做的？', '皮革', '布料', '草或棕毛', '纸张', 'C', '张志和《渔歌子》，蓑衣为传统草编防雨工具。');
INSERT INTO "zhishitwo" VALUES (53, '以下哪些诗句与月亮有关？（多选）', 'A. 床前明月光', 'B. 举头望明月', 'C. 明月几时有', 'D. 月落乌啼霜满天', 'ABCD', '分别出自李白、苏轼、张继诗作，均含月亮意象。');
INSERT INTO "zhishitwo" VALUES (54, '“何当共剪西窗烛，却话巴山夜雨时。”中的“巴山”在哪里？', '贵州境内', '重庆境内', '湖北境内', '四川境内', 'D', '李商隐《夜雨寄北》，“巴山”指四川东部山脉。');
INSERT INTO "zhishitwo" VALUES (55, '“醉卧沙场君莫笑，古来征战几人回。”表达了诗人怎样的情感？', '对胜利的渴望', '对战争的无奈和悲壮', '对生命的珍视', '对死亡的恐惧', 'B', '王翰《凉州词》，豪放中隐含对战争残酷的悲叹。');
INSERT INTO "zhishitwo" VALUES (56, '“千里黄云白日曛，北风吹雁雪纷纷。”中的“黄云”指什么？', '黄沙弥漫的天空', '黄色的云彩', '秋天的落叶', '夕阳的余晖', 'A', '高适《别董大》，描绘塞外风沙蔽日的昏黄天空。');
INSERT INTO "zhishitwo" VALUES (57, '“江南好，风景旧曾谙。日出江花红胜火，”下一句是什么？', '秋来江水碧如玉', '春来江水绿如蓝', '夏来江水清如镜', '冬来江水白如银', 'B', '白居易《忆江南》，写江南春水碧绿如蓝的美景。');
INSERT INTO "zhishitwo" VALUES (58, '“葡萄美酒夜光杯，欲饮琵琶马上催。”中的“琵琶”起什么作用？', '礼品', '伴奏', '装饰', '助兴', 'D', '王翰《凉州词》，琵琶声为宴饮增添豪迈氛围。');
INSERT INTO "zhishitwo" VALUES (59, '“白日依山尽，黄河入海流。欲穷千里目，”下一句是什么？', '更上一层楼', '再望一眼远', '遥看天际边', '静待日落时', 'A', '王之涣《登鹳雀楼》，登高望远蕴含哲理。');
INSERT INTO "zhishitwo" VALUES (60, '“随风潜入夜，润物细无声。”描述的是什么？', '秋雨', '夏雨', '春雨', '冬雪', 'C', '杜甫《春夜喜雨》，细腻刻画春雨润物特点。');
INSERT INTO "zhishitwo" VALUES (61, '“野径云俱黑，江船火独明。”与哪句诗意境相似？', 'A. 孤舟蓑笠翁，独钓寒江雪', 'B. 明月松间照，清泉石上流', 'C. 柴门闻犬吠，风雪夜归人', 'D. 千山鸟飞绝，万径人踪灭', 'C', '均以孤灯/犬吠反衬静谧与孤寂（杜甫《春夜喜雨》/刘长卿《逢雪宿芙蓉山主人》）。');
INSERT INTO "zhishitwo" VALUES (62, '“接天莲叶无穷碧，映日荷花别样红。”描述的是哪个季节？', '春天', '夏天', '秋天', '冬天', 'B', '杨万里《晓出净慈寺送林子方》，写盛夏西湖荷花盛景。');
INSERT INTO "zhishitwo" VALUES (63, '“劝君更尽一杯酒，西出阳关无故人。”中的“阳关”在哪里？', '甘肃', '新疆', '内蒙古', '宁夏', 'A', '阳关位于今甘肃省敦煌市，是古代通往西域的重要关隘。王维《送元二使安西》以此喻友人远行的苍凉。');
INSERT INTO "zhishitwo" VALUES (64, '以下哪些诗句出自《春夜喜雨》？（多选）', '好雨知时节', '当春乃发生', '随风潜入夜', '润物细无声', 'ABCD', '杜甫《春夜喜雨》全诗为：“好雨知时节，当春乃发生。随风潜入夜，润物细无声。野径云俱黑，江船火独明。晓看红湿处，花重锦官城。”四句均出自本诗。');
INSERT INTO "zhishitwo" VALUES (65, '“清明时节雨纷纷，路上行人欲断魂。”表达了诗人怎样的情感？', '思念亲人', '悲伤孤独', '旅途艰辛', '对春天的赞美', 'A', '杜牧《清明》，“欲断魂”体现清明扫墓时对逝去亲人的哀思与怀念。');
INSERT INTO "zhishitwo" VALUES (66, '“遥望洞庭山水翠，白银盘里一青螺。”中的“青螺”指什么？', '湖中的水草', '青色的螺蛳', '远处的山峰', '洞庭湖中的小岛', 'D', '刘禹锡《望洞庭》，“青螺”比喻洞庭湖中的君山，以“白银盘”喻湖水，展现湖光山色之美。');
INSERT INTO "zhishitwo" VALUES (67, '“儿童相见不相识，笑问客从何处来。”中的“客”指谁？', '诗人自己', '诗人的朋友', '诗人的亲人', '过路的行人', 'A', '贺知章《回乡偶书》，诗人年老归乡，儿童不识其容貌，以“客”自指，感慨时光流逝。');
INSERT INTO "zhishitwo" VALUES (68, '以下哪些诗句与送别有关？（多选）', '海内存知己', '天涯若比邻', '劝君更尽一杯酒', '西出阳关无故人', 'ABCD', 'A、B出自王勃《送杜少府之任蜀州》，C、D出自王维《送元二使安西》，均为经典送别诗句。');
INSERT INTO "zhishitwo" VALUES (69, '“孤帆远影碧空尽，唯见长江天际流。”中的“孤帆”象征什么？', '远去的朋友', '孤独的旅人', '一艘孤船', '长江的波浪', 'A', '李白《黄鹤楼送孟浩然之广陵》，“孤帆”象征友人离去的孤独身影，以江水寄托深情。');
INSERT INTO "zhishitwo" VALUES (70, '“野火烧不尽，春风吹又生。”描述了哪种植物的顽强？', '小草', '大树', '花朵', '竹子', 'A', '白居易《赋得古原草送别》，借野草的生命力表达对友情的坚定信念。');
INSERT INTO "zhishitwo" VALUES (71, '“白日放歌须纵酒，青春作伴好还乡。”中的“青春”指什么？', '年轻的时光', '春天的景色', '青春的朋友', '充满活力的状态', 'B', '杜甫《闻官军收河南河北》，“青春”指春日美景，与“还乡”呼应，表现诗人欣喜之情。');
INSERT INTO "zhishitwo" VALUES (72, '“南朝四百八十寺，多少楼台烟雨中。”中的“南朝”指哪个朝代？', '南宋', '南齐、梁、陈等', '南北朝时期的南方', '南明', 'B', '杜牧《江南春》，“南朝”指宋、齐、梁、陈四朝，借古寺烟雨讽喻统治者崇佛误国。');
INSERT INTO "zhishitwo" VALUES (73, '“采菊东篱下，悠然见南山。”中的“南山”在哪里？', '诗人心中的理想山', '实际的某座山', '泛指南方的山', '特定的名山', 'B', '陶渊明《饮酒》，“南山”实指庐山，象征超脱尘世的隐逸心境。');
INSERT INTO "zhishitwo" VALUES (74, '以下哪些诗句出自《赋得古原草送别》？（多选）', 'A. 离离原上草', 'B. 一岁一枯荣', 'C. 野火烧不尽', 'D. 春风吹又生', 'ABCD', '白居易《赋得古原草送别》全诗为：“离离原上草，一岁一枯荣。野火烧不尽，春风吹又生。远芳侵古道，晴翠接荒城。又送王孙去，萋萋满别情。”四句均出自本诗。');
INSERT INTO "zhishitwo" VALUES (75, '“黑云压城城欲摧，甲光向日金鳞开。”中的“甲光”指什么？', '战士铠甲的反光', '士兵的眼神', '城墙的反光', '武器的锋芒', 'A', '李贺《雁门太守行》，“甲光”指战士铠甲在日光下的反光，渲染战场肃杀氛围。');
INSERT INTO "zhishitwo" VALUES (76, '“秦时明月汉时关，万里长征人未还。”表达了诗人怎样的情感？', '对边疆战士的同情', '对战争的厌恶', '对家乡的思念', '对胜利的渴望', 'AC', '王昌龄《出塞》，既同情戍边战士的艰辛（A），又隐含对战争的反思（C）。');
INSERT INTO "zhishitwo" VALUES (77, '“独怜幽草涧边生，上有黄鹂深树鸣。”中的“幽草”指什么？', '生长在涧边的草', '幽静的草地', '珍稀的草药', '深山里的野草', 'A', '韦应物《滁州西涧》，“幽草”指涧边自甘寂寞的野草，暗喻诗人恬淡的志趣。');
INSERT INTO "zhishitwo" VALUES (78, '“春风又绿江南岸，明月何时照我还？”中的“绿”字用得好在哪里？', '形象地描绘了春天的景色', '表现了春天的生机', '与“红”字形成对比', '突出了江南的特色', 'AB', '王安石《泊船瓜洲》，“绿”字化静为动，生动展现春风吹绿江南的动态美（A、B）。');
INSERT INTO "zhishitwo" VALUES (79, '“千山鸟飞绝，万径人踪灭。”描述了什么景象？', '冬天的寂静', '春天的繁华', '夏天的热闹', '秋天的萧瑟', 'A', '柳宗元《江雪》，以“绝”“灭”描绘寒冬万籁俱寂的苍茫雪景。');
INSERT INTO "zhishitwo" VALUES (80, '“孤舟蓑笠翁，独钓寒江雪。”中的“蓑笠翁”形象如何？', '孤独而坚韧', '悠闲而自在', '贫穷而可怜', '高傲而冷漠', 'A', '柳宗元借“蓑笠翁”在寒江独钓的形象，表现自己孤傲不屈的精神品格。');
INSERT INTO "zhishitwo" VALUES (81, '“明月松间照，清泉石上流。”中的“明月”和“清泉”象征什么？', '高洁的品质', '自然的美丽', '宁静的心境', '生活的悠闲', 'ABC', '王维《山居秋暝》，“明月”象征高洁品格，“清泉”象征心境澄澈，整体描绘自然山水之美。');
INSERT INTO "zhishitwo" VALUES (82, '“黄沙百战穿金甲，不破楼兰终不还。”中的“楼兰”指什么？', '古代的一个国家', '一座坚固的城池', '一个凶猛的部落', '一片荒芜的沙漠', 'A', '王昌龄《从军行》，“楼兰”是汉代西域古国，代指边疆敌人。');
INSERT INTO "zhishitwo" VALUES (83, '“竹外桃花三两枝，春江水暖鸭先知。”中的“鸭先知”指什么？', '鸭子最先感知到水温', '鸭子最聪明', '鸭子最喜欢春天', '鸭子最会游泳', 'A', '苏轼《惠崇春江晚景》，借鸭子感知春江水暖，表现早春生机。');
INSERT INTO "zhishitwo" VALUES (84, '“梅子金黄杏子肥，麦花雪白菜花稀。”描述了哪个季节的景色？', '春天', '夏天', '秋天', '冬天', 'B', '范成大《四时田园杂兴》，梅子、杏子成熟于夏季，麦花、菜花为夏季作物。');
INSERT INTO "zhishitwo" VALUES (85, '“四海无闲田，农夫犹饿死。”表达了诗人怎样的情感？', '对农夫的同情', '对社会的不满', '对丰收的喜悦', '对自然的赞美', 'AB', '李绅《悯农》，揭露社会不公与农民疾苦。');
INSERT INTO "zhishitwo" VALUES (86, '“茅檐低小，溪上青青草。”中的“茅檐”指什么？', '茅草屋的屋檐', '茅草做的帘子', '茅草地的边缘', '茅草编织的装饰品', 'A', '辛弃疾《清平乐·村居》，“茅檐”指农家茅屋的屋檐，体现朴素田园生活。');
INSERT INTO "zhishitwo" VALUES (87, '“大江东去，浪淘尽，千古风流人物。”中的“大江”指哪条江？', '长江', '黄河', '珠江', '淮河', 'A', '苏轼《念奴娇·赤壁怀古》，“大江”即长江，借江水咏叹历史兴亡。');
INSERT INTO "zhishitwo" VALUES (88, '“西塞山前白鹭飞，桃花流水鳜鱼肥。”中的“鳜鱼”是什么鱼？', '一种淡水鱼', '一种海水鱼', '一种观赏鱼', '一种热带鱼', 'A', '张志和《渔歌子》，鳜鱼为江南淡水鱼，肉质鲜美，象征春景富饶。');
INSERT INTO "zhishitwo" VALUES (89, '“暖风熏得游人醉，直把杭州作汴州。”中的“汴州”是哪里？', '开封', '杭州', '洛阳', '南京', 'A', '林升《题临安邸》，“汴州”指北宋都城汴京（今开封），讽刺南宋统治者偏安一隅。');
INSERT INTO "zhishitwo" VALUES (90, '“生当作人杰，死亦为鬼雄。”表达了诗人怎样的志向？', '英勇无畏', '慷慨就义', '追求卓越', '宁死不屈', 'ACD', '李清照《夏日绝句》，以人杰、鬼雄自喻，表达刚烈不屈的志向。');
INSERT INTO "zhishitwo" VALUES (91, '“床前明月光，疑是地上霜。”中的“疑”字表达了诗人怎样的心情？', '疑惑不解', '惊喜交加', '思念家乡', '孤独寂寞', 'A', '李白《静夜思》，“疑”字生动表现诗人恍惚中将月光误认为霜的瞬间。');
INSERT INTO "zhishitwo" VALUES (92, '“举头望明月，低头思故乡。”这句诗出自哪首古诗？', '《静夜思》', '《望庐山瀑布》', '《夜泊牛渚怀古》', '《月下独酌》', 'A', '李白《静夜思》，以明月寄寓思乡之情。');
INSERT INTO "zhishitwo" VALUES (93, '“飞流直下三千尺，疑是银河落九天。”中的“三千尺”是实指吗？', '是，确实有三千尺', '否，是夸张说法', '不确定', '可能是实指也可能不是', 'B', '李白《望庐山瀑布》，用夸张手法突出瀑布的雄伟气势。');
INSERT INTO "zhishitwo" VALUES (94, '以下哪些诗句出自《早发白帝城》？（多选）', 'A. 朝辞白帝彩云间', 'B. 千里江陵一日还', 'C. 两岸猿声啼不住', 'D. 轻舟已过万重山', 'ABCD', '李白《早发白帝城》全诗四句均列于选项中，写三峡行舟之迅疾。');
INSERT INTO "zhishitwo" VALUES (95, '“野径云俱黑，江船火独明。”中的“野径”指什么？', '野外的小路', '荒野中的河流', '远处的山峰', '茂密的森林', 'A', '杜甫《春夜喜雨》，“野径”指郊外小路，与“江船火”形成明暗对比。');
INSERT INTO "zhishitwo" VALUES (96, '“春风得意马蹄疾，一日看尽长安花。”表达了诗人怎样的心情？', '思念亲人', '忧伤沮丧', '心情愉悦', '愤怒不满', 'C', '孟郊《登科后》，写科举高中后策马游街的畅快之情。');
INSERT INTO "zhishitwo" VALUES (97, '“深林人不知，明月来相照。”中的“明月”象征着什么？', '高洁的品质', '孤独的心情', '宁静的夜晚', '光明的希望', 'AD', '王维《竹里馆》，“明月”既象征诗人高洁的品格（A），也暗示孤寂中的精神寄托（D）。');
INSERT INTO "zhishitwo" VALUES (98, '“故人具鸡黍，邀我至田家。”中的“鸡黍”指什么？', '鸡肉和蔬菜', '鸡肉和米饭', '猪肉和米饭', '鱼肉和面条', 'B', '孟浩然《过故人庄》，“鸡黍”是农家待客的简朴饭食，体现田园情谊。');
INSERT INTO "zhishitwo" VALUES (99, '“绿树村边合，青山郭外斜。”描述了怎样的乡村景象？', '绿树环绕村庄', '青山在城郭外', '村庄热闹非凡', '田野一片荒凉', 'AB', '孟浩然《过故人庄》，前句写绿树环村（A），后句写青山斜倚城郭（B）。');
INSERT INTO "zhishitwo" VALUES (100, '“开轩面场圃，把酒话桑麻。”中的“桑麻”指什么？', '麻绳和布匹', '桑树和庄稼', '桑树和麻', '桑葚和麻花', 'C', '孟浩然《过故人庄》，“桑麻”泛指农事，体现田园生活的质朴。');
INSERT INTO "zhishitwo" VALUES (101, '“待到重阳日，还来就菊花。”中的“重阳日”是哪天？', '农历正月初一', '农历八月十五', '农历五月初五', '农历九月初九', 'D', '孟浩然《过故人庄》，重阳节为农历九月初九，有登高、赏菊习俗。');
INSERT INTO "zhishitwo" VALUES (102, '“白日依山尽，黄河入海流。”中的“依”字用得好在哪里？', '形象地描绘了日落', '表现了山的雄伟', '突出了黄河的壮阔', '与“尽”字形成对比', 'A', '王之涣《登鹳雀楼》，“依”字拟人化描写夕阳傍山沉落的动态美（A）。');
INSERT INTO "zhishitwo" VALUES (103, '“欲穷千里目，更上一层楼。”表达了诗人怎样的哲理？', '要善待他人', '要不断努力', '要珍惜时间', '站得高看得远', 'D', '王之涣《登鹳雀楼》，通过登高望远的场景，表达“站得高看得远”的哲理，鼓励人们不断进取。');
INSERT INTO "zhishitwo" VALUES (104, '“黄河远上白云间，一片孤城万仞山。”中的“孤城”指什么？', '一片荒芜的沙漠', '一座繁华的城市', '一座孤立的城池', '一座高山上的庙宇', 'C', '王之涣《凉州词》，“孤城”指边疆的孤立城池，象征戍边将士的孤独与坚守。');
INSERT INTO "zhishitwo" VALUES (105, '“羌笛何须怨杨柳，春风不度玉门关。”中的“杨柳”象征什么？', '离别和思乡之情', '春天的美景', '坚韧不拔的品质', '高洁的人格', 'A', '王之涣《凉州词》，“杨柳”象征离别之情，暗指戍边将士对家乡的思念。');
INSERT INTO "zhishitwo" VALUES (106, '“野火烧不尽，春风吹又生。”这句诗出自哪首古诗？', '《江南春》', '《春夜喜雨》', '《赋得古原草送别》', '《早春呈水部张十八员外》', 'C', '白居易《赋得古原草送别》，借野草的生命力表达对友情的坚定信念。');
INSERT INTO "zhishitwo" VALUES (107, '“天街小雨润如酥，草色遥看近却无。”描述了怎样的景色？', '春雨细腻滋润', '草色若隐若现', '街道热闹非凡', '天空晴朗明媚', 'AB', '韩愈《早春呈水部张十八员外》，描绘早春细雨滋润大地、草色朦胧的美景。');
INSERT INTO "zhishitwo" VALUES (108, '“最是一年春好处，绝胜烟柳满皇都。”中的“皇都”指哪里？', '南京', '洛阳', '长安', '京城', 'D', '韩愈《早春呈水部张十八员外》，“皇都”指京城，赞美早春景色胜过繁华都城。');
INSERT INTO "zhishitwo" VALUES (109, '“卷地风来忽吹散，望湖楼下水如天。”描述了怎样的景象？', '楼下人声鼎沸', '湖面波涛汹涌', '风雨过后湖面平静', '天空乌云密布', 'C', '苏轼《六月二十七日望湖楼醉书》，写风雨过后湖面恢复平静，水天一色的壮丽景象。');
INSERT INTO "zhishitwo" VALUES (110, '“山外青山楼外楼，西湖歌舞几时休？”表达了诗人怎样的情感？', '对国家命运的担忧', '对西湖美景的赞美', '对歌舞升平的向往', '对生活的热爱', 'A', '林升《题临安邸》，借西湖歌舞讽刺南宋统治者偏安一隅，忽视国家危亡。');
INSERT INTO "zhishitwo" VALUES (111, '“暖风熏得游人醉，直把杭州作汴州。”中的“游人”指谁？', '普通的游客', '南宋的统治阶级', '外来的商人', '当地的居民', 'B', '林升《题临安邸》，“游人”指南宋统治者，讽刺他们沉醉于享乐，忘却国耻。');
INSERT INTO "zhishitwo" VALUES (112, '“生当作人杰，死亦为鬼雄。”这句诗出自哪位诗人的作品？', '李清照', '杜甫', '李白', '王维', 'A', '李清照《夏日绝句》，以项羽宁死不屈的精神自勉，表达对英雄的崇敬。');
INSERT INTO "zhishitwo" VALUES (113, '“至今思项羽，不肯过江东。”表达了诗人怎样的态度？', '对项羽的敬佩', '对项羽的同情', '对江东的厌恶', '对历史的感慨', 'AB', '李清照《夏日绝句》，既敬佩项羽的英雄气概，又同情他的悲剧命运。');
INSERT INTO "zhishitwo" VALUES (114, '“煮豆燃豆萁，豆在釜中泣。”中的“豆萁”指什么？', '豆子的根', '豆子的叶子', '煮豆子的锅', '豆子的茎秆', 'D', '曹植《七步诗》，“豆萁”指豆茎，比喻兄弟相残的残酷现实。');
INSERT INTO "zhishitwo" VALUES (115, '“本是同根生，相煎何太急？”表达了诗人怎样的情感？', '兄弟之间的深情', '对战争的厌恶', '对生活的无奈', '对命运的感慨', 'A', '曹植《七步诗》，以豆萁相煎喻兄弟相争，表达对亲情的珍视与无奈。');
INSERT INTO "zhishitwo" VALUES (116, '“爆竹声中一岁除，春风送暖入屠苏。”中的“屠苏”指什么？', '一种乐器', '一种食物', '一种酒', '一种花卉', 'C', '王安石《元日》，“屠苏”指屠苏酒，古代春节饮用以祈福祛病。');
INSERT INTO "zhishitwo" VALUES (117, '“千门万户曈曈日，总把新桃换旧符。”描述了怎样的节日景象？', '元宵节的灯会', '中秋节的团圆', '端午节的赛龙舟', '春节的热闹', 'D', '王安石《元日》，描绘春节家家户户贴春联、放鞭炮的喜庆场景。');
INSERT INTO "zhishitwo" VALUES (118, '“蒌蒿满地芦芽短，正是河豚欲上时。”描述了哪个季节的景色？', '秋天', '夏天', '春天', '冬天', 'C', '苏轼《惠崇春江晚景》，写春天蒌蒿、芦芽生长的景象，暗示河豚肥美的时节。');
INSERT INTO "zhishitwo" VALUES (119, '“独在异乡为异客，每逢佳节倍思亲。”中的“佳节”指的是？', '中秋节', '重阳节', '春节', '端午节', 'B', '王维《九月九日忆山东兄弟》，“佳节”指重阳节，表达对亲人的深切思念。');
INSERT INTO "zhishitwo" VALUES (120, '“遥望洞庭山水翠，白银盘里一青螺。”中的“青螺”比喻什么？', '银色的盘子', '远处的山峰', '湖中的水草', '洞庭湖中的岛屿', 'D', '刘禹锡《望洞庭》，“青螺”比喻湖中的君山，展现湖光山色的优美意境。');
INSERT INTO "zhishitwo" VALUES (121, '“湖光秋月两相和，潭面无风镜未磨。”描述了哪里的景色？', '西湖', '洞庭湖', '太湖', '鄱阳湖', 'B', '刘禹锡《望洞庭》，描绘秋夜洞庭湖的静谧与壮丽。');
INSERT INTO "zhishitwo" VALUES (122, '“洛阳亲友如相问，一片冰心在玉壶。”表达了诗人怎样的品质？', '思念家乡', '孤独寂寞', '高洁清廉', '忧国忧民', 'C', '王昌龄《芙蓉楼送辛渐》，以“冰心在玉壶”自喻，表达自己高洁的品格。');
INSERT INTO "zhishitwo" VALUES (123, '“寒雨连江夜入吴，平明送客楚山孤。”中的“楚山”指哪里？', '长江边的山', '具体的某座山名', '泛指南方的山', '楚地的山,江南的山', 'D', '王昌龄《芙蓉楼送辛渐》，“楚山”泛指江南的山，象征送别时的孤寂与离愁。');
INSERT INTO "zhishitwo" VALUES (124, '“野旷天低树，江清月近人。”中的“近人”表达了什么情感？', '亲近自然', '孤独寂寞', '思念亲人', '对月亮的喜爱', 'B', '孟浩然《宿建德江》，“近人”指月亮仿佛与人亲近，反衬诗人的孤独寂寞。');
INSERT INTO "zhishitwo" VALUES (125, '“春潮带雨晚来急，野渡无人舟自横。”描述了怎样的景象？', '野渡口的热闹', '春日傍晚的繁忙', '春雨后的宁静', '江面上的波涛汹涌', 'C', '韦应物《滁州西涧》，写春雨后野渡无人、小舟自横的静谧景象。');
INSERT INTO "zhishitwo" VALUES (126, '“不要人夸好颜色，只留清气满乾坤。”出自哪位诗人的作品？', '王冕', '李白', '杜甫', '王维', 'A', '王冕《墨梅》，以梅花自喻，表达不慕虚名、坚守高洁的情操。');
INSERT INTO "zhishitwo" VALUES (127, '“采得百花成蜜后，为谁辛苦为谁甜？”中的“谁”指什么？', '蜜蜂的主人', '赏花的人', '劳动人民', '大自然', 'C', '罗隐《蜂》，借蜜蜂采蜜喻劳动人民的辛勤付出与无奈。');
INSERT INTO "zhishitwo" VALUES (128, '“黑云压城城欲摧，甲光向日金鳞开。”中的“金鳞”比喻什么？', '金色的鱼鳞', '战士铠甲在日光下的反光', '城墙上的装饰', '战士的勇气', 'B', '李贺《雁门太守行》，“金鳞”指战士铠甲在日光下的反光，渲染战场肃杀氛围。');
INSERT INTO "zhishitwo" VALUES (129, '“角声满天秋色里，塞上燕脂凝夜紫。”描述了怎样的战场景象？', '塞外的风光', '秋天的宁静', '战争的惨烈', '夜晚的寂静', 'C', '李贺《雁门太守行》，以“角声”“燕脂”等意象描绘战争的残酷与悲壮。');
INSERT INTO "zhishitwo" VALUES (130, '“半亩方塘一鉴开，天光云影共徘徊。”中的“鉴”指什么？', '池塘', '镜子', '天空', '云彩', 'B', '朱熹《观书有感》，“鉴”比喻池塘如镜子般清澈，映照天光云影。');
INSERT INTO "zhishitwo" VALUES (131, '“问渠那得清如许？为有源头活水来。”中的“渠”指什么？', '水的源头', '水渠', '渠道（人工开凿）', '池塘或河流', 'D', '朱熹《观书有感》，“渠”指池塘，强调活水源源不断的重要性。');
INSERT INTO "zhishitwo" VALUES (132, '“纸上得来终觉浅，绝知此事要躬行。”表达了怎样的哲理？', '实践出真知', '读书无用', '理论重要', '经验可贵但理论更重', 'A', '陆游《冬夜读书示子聿》，强调实践是获取真知的唯一途径。');
INSERT INTO "zhishitwo" VALUES (133, '“三万里河东入海，五千仞岳上摩天。”中的“河”和“岳”分别指？', '黄河,华山', '长江,华山', '黄河,泰山', '长江,泰山', 'C', '陆游《秋夜将晓出篱门迎凉有感》，“河”指黄河，“岳”指泰山，展现祖国山河的壮丽。');
INSERT INTO "zhishitwo" VALUES (134, '“遗民泪尽胡尘里，南望王师又一年。”表达了怎样的情感？', '对故国的思念', '对战争的厌恶', '对王师的期盼', '对生活的无奈', 'AC', '陆游《秋夜将晓出篱门迎凉有感》，既表达对故国的思念，又隐含对王师的期盼。');
INSERT INTO "zhishitwo" VALUES (135, '“王师北定中原日，家祭无忘告乃翁。”中的“乃翁”指谁？', '诗人自己（陆游）', '诗人的父亲', '诗人的祖父', '诗人的老师', 'A', '陆游《示儿》，“乃翁”即诗人自己，表达临终前对收复中原的期望。');
INSERT INTO "zhishitwo" VALUES (136, '“牧童骑黄牛，歌声振林樾。”描述了怎样的画面？', '牧童在唱歌比赛', '黄牛在林中奔跑', '牧童放牛的情景', '林中鸟儿在欢唱', 'C', '袁枚《所见》，描绘牧童骑牛唱歌的田园生活场景。');
INSERT INTO "zhishitwo" VALUES (137, '“意欲捕鸣蝉，忽然闭口立。”中的“立”字用得好在哪里？', '表现了牧童的专注', '突出了蝉的叫声大', '描绘了牧童的姿势', '与“闭口”形成对比', 'A,C', '袁枚《所见》，“立”字生动表现牧童专注捕蝉的瞬间动作。');
INSERT INTO "zhishitwo" VALUES (138, '“舟夜书所见”中的“书”指什么？', '画画', '看书', '写信', '写诗', 'D', '查慎行《舟夜书所见》，“书”指写诗，记录夜晚所见之景。');
INSERT INTO "zhishitwo" VALUES (139, '“月黑见渔灯，孤光一点萤。”描述了怎样的夜晚景象？', '月亮很亮照见渔灯', '漆黑的夜晚有渔灯', '萤火虫很多', '夜晚很热闹', 'B', '查慎行《舟夜书所见》，写月黑之夜渔灯如萤的静谧景象。');
INSERT INTO "zhishitwo" VALUES (140, '“微微风簇浪，散作满河星。”中的“星”比喻什么？', '河底的石头', '天上的星星', '渔船上的灯火', '河面上的波光', 'D', '查慎行《舟夜书所见》，“星”比喻微风吹动水面泛起的粼粼波光。');
INSERT INTO "zhishitwo" VALUES (141, '“九州生气恃风雷，万马齐喑究可哀。”中的“九州”指什么？', '中国', '九个州', '九个地方', '九个国家', 'A', '龚自珍《己亥杂诗》，“九州”代指中国，表达对国家变革的期望。');
INSERT INTO "zhishitwo" VALUES (142, '“我劝天公重抖擞，不拘一格降人材。”表达了诗人怎样的愿望？', '希望国家统一', '希望天公下雨', '希望国家重用人才', '希望世界和平', 'C', '龚自珍《己亥杂诗》，呼吁国家不拘一格选拔人才，振兴国家。');
INSERT INTO "zhishitwo" VALUES (143, '“千锤万凿出深山，烈火焚烧若等闲。”中的“若等闲”意思是？', '好像很平常', '好像很轻松', '好像很容易', '好像无所谓', 'A', '于谦《石灰吟》，“若等闲”形容石灰石在烈火中焚烧的从容，象征坚毅精神。');
INSERT INTO "zhishitwo" VALUES (144, '“粉骨碎身浑不怕，要留清白在人间。”表达了怎样的品质？', '坚贞不屈', '英勇无畏', '清白正直', '无私奉献', 'C', '于谦《石灰吟》，以石灰自喻，表达坚守清白、正直不阿的品质。');
INSERT INTO "zhishitwo" VALUES (145, '“竹外桃花三两枝，春江水暖鸭先知。”中的“先知”指什么？', '鸭子先知道水温', '人先知道春天来', '桃花先开放', '竹子先长高', 'A', '苏轼《惠崇春江晚景》，借鸭子感知春江水暖，表现早春生机。');
INSERT INTO "zhishitwo" VALUES (146, '“蒌蒿满地芦芽短，正是河豚欲上时。”中的“上”指什么？', '河豚上游', '河豚上岸', '河豚上市（季节）', '河豚长大', 'C', '苏轼《惠崇春江晚景》，“上”指河豚上市的季节，暗示春天的到来。');
INSERT INTO "zhishitwo" VALUES (147, '“日长篱落无人过，惟有蜻蜓蛱蝶飞。”中的“日长”指什么？', '天气很热', '太阳很大', '日子很长', '白天时间长', 'D', '范成大《四时田园杂兴》，“日长”指夏季白天时间变长，表现乡村的宁静。');
INSERT INTO "zhishitwo" VALUES (148, '“大儿锄豆溪东，中儿正织鸡笼。”出自哪首古诗？', '《四时田园杂兴》', '《乡村四月》', '《村居》', '《所见》', 'A', '范成大《四时田园杂兴》，描绘农家劳作的田园生活场景。');
INSERT INTO "zhishitwo" VALUES (149, '“独怜幽草涧边生，上有黄鹂深树鸣。”中的“独怜”表达了诗人怎样的情感？', '孤独寂寞', '对幽草的喜爱', '对黄鹂的欣赏', '对深树的敬畏', 'B', '韦应物《滁州西涧》，“独怜”表达诗人对涧边幽草的偏爱，体现其恬淡的心境。');
INSERT INTO "zhishitwo" VALUES (150, '“春潮带雨晚来急，野渡无人舟自横。”这句诗出自哪位诗人的作品？', '韦应物', '李白', '杜甫', '王维', 'A', '韦应物《滁州西涧》，描绘春雨后野渡无人、小舟自横的静谧景象。');
INSERT INTO "zhishitwo" VALUES (151, '“黑云翻墨未遮山，白雨跳珠乱入船。”中的“白雨”指的是什么？', '白色的雨', '大雨如注，雨点像白色的珠子', '小雨绵绵', '雨后的白云', 'B', '苏轼《六月二十七日望湖楼醉书》，“白雨”形容大雨点如白色珠子，渲染雨势的猛烈。');
INSERT INTO "zhishitwo" VALUES (152, '“毕竟西湖六月中，风光不与四时同。”中的“毕竟”表达了什么意思？', '到底、终究', '毕竟、还是', '不过、只是', '当然、一定', 'A', '杨万里《晓出净慈寺送林子方》，“毕竟”强调西湖六月风光的独特与美好。');
INSERT INTO "zhishitwo" VALUES (153, '“接天莲叶无穷碧，映日荷花别样红。”这句诗出自哪首古诗？', '《小池》', '《春日》', '《晓出净慈寺送林子方》', '《江南》', 'C', '杨万里《晓出净慈寺送林子方》，描绘西湖夏日莲叶与荷花的壮丽景色。');
INSERT INTO "zhishitwo" VALUES (154, '“泉眼无声惜细流，树阴照水爱晴柔。”中的“惜”字用得好在哪里？', '表现了泉眼的珍惜', '突出了泉水的清澈', '描绘了泉眼的安静', '与“爱”字相呼应', 'A', '杨万里《小池》，“惜”字拟人化描写泉眼对细流的珍惜，赋予自然景物以情感。');
INSERT INTO "zhishitwo" VALUES (155, '“小荷才露尖尖角，早有蜻蜓立上头。”描述了怎样的画面？', '荷花初绽，蜻蜓停留', '荷花盛开，蝴蝶飞舞', '荷叶茂盛，青蛙跳跃', '荷塘月色，宁静美好', 'A', '杨万里《小池》，描绘初夏荷塘的生机盎然，蜻蜓与荷花相映成趣。');
INSERT INTO "zhishitwo" VALUES (156, '“两个黄鹂鸣翠柳，一行白鹭上青天。”中的“青天”指的是什么？', '蓝色的天空', '高高的天空', '晴朗的天空', '广阔的天空', 'C', '杜甫《绝句》，“青天”指晴朗的天空，展现春天的明媚与生机。');
INSERT INTO "zhishitwo" VALUES (157, '“窗含西岭千秋雪，门泊东吴万里船。”中的“西岭”指的是哪里？', '成都西面的岷山', '西方的山岭', '某个具体的地名', '诗人想象中的山', 'A', '杜甫《绝句》，“西岭”指成都西面的岷山，以“千秋雪”象征其高峻与永恒。');
INSERT INTO "zhishitwo" VALUES (158, '“远上寒山石径斜，白云深处有人家。”中的“斜”字表达了什么意思？', '石径的倾斜', '远处的斜阳', '山的斜坡', '诗人的心情倾斜', 'A', '杜牧《山行》，“斜”字描绘石径的蜿蜒曲折，展现山行的动态美。');
INSERT INTO "zhishitwo" VALUES (159, '“停车坐爱枫林晚，霜叶红于二月花。”中的“坐”字应如何理解？', '坐下', '因为', '乘坐', '驻足', 'B', '杜牧《山行》，“坐”意为“因为”，表达诗人因喜爱枫林晚景而停车观赏。');
INSERT INTO "zhishitwo" VALUES (160, '“寒菊郑思肖花开不并百花丛，独立疏篱趣未穷。”中的“趣”指的是什么？', '乐趣、情趣', '趣味、好玩', '志向、追求', '意义、价值', 'A', '郑思肖《寒菊》，“趣”指菊花独立疏篱的乐趣与情趣，象征高洁品格。');
INSERT INTO "zhishitwo" VALUES (161, '“宁可枝头抱香死，何曾吹落北风中。”表达了菊花怎样的品质？', '随风飘摇', '娇艳美丽', '坚贞不屈', '弱不禁风', 'C', '郑思肖《寒菊》，以菊花自喻，表达坚守气节、宁死不屈的精神。');
INSERT INTO "zhishitwo" VALUES (162, '“生当作人杰，死亦为鬼雄。”出自哪位女诗人的作品？', '李清照', '鱼玄机', '薛涛', '朱淑真', 'A', '李清照《夏日绝句》，以项羽宁死不屈的精神自勉，表达对英雄的崇敬。');
INSERT INTO "zhishitwo" VALUES (163, '“至今思项羽，不肯过江东。”中的“项羽”是哪个历史时期的人物？', '三国时期', '汉朝初年', '秦朝末年', '唐朝', 'C', '项羽是秦朝末年著名的军事家，楚汉争霸的核心人物。');
INSERT INTO "zhishitwo" VALUES (164, '“天街小雨润如酥，草色遥看近却无。”中的“天街”指的是什么？', '宽阔的道路', '天上的街道', '京城的大街', '繁华的市集', 'C', '韩愈《早春呈水部张十八员外》，“天街”指京城的大街，描绘早春细雨滋润的景象。');
INSERT INTO "zhishitwo" VALUES (165, '“最是一年春好处，绝胜烟柳满皇都。”中的“皇都”指的是哪里？', '皇宫', '京城', '皇城', '皇帝的住所', 'B', '韩愈《早春呈水部张十八员外》，“皇都”指京城，赞美早春景色胜过繁华都城。');
INSERT INTO "zhishitwo" VALUES (166, '“草长莺飞二月天，拂堤杨柳醉春烟。”描述了怎样的春日景象？', '草木生长，黄莺飞舞', '杨柳依依，春烟缭绕', '春暖花开，鸟语花香', '春风和煦，阳光明媚', 'A', '高鼎《村居》，描绘春天草木茂盛、黄莺飞舞的生机盎然景象。');
INSERT INTO "zhishitwo" VALUES (167, '“儿童散学归来早，忙趁东风放纸鸢。”中的“纸鸢”指的是什么？', '纸做的装饰物', '纸飞机', '纸船', '风筝', 'D', '高鼎《村居》，“纸鸢”即风筝，描绘儿童春日放风筝的欢乐场景。');
INSERT INTO "zhishitwo" VALUES (168, '“牧童归去横牛背，短笛无腔信口吹。”中的“信口吹”表达了什么意思？', '吹得不太熟练', '按照曲谱吹奏', '吹得很好听', '随意吹奏', 'D', '雷震《村晚》，“信口吹”指牧童随意吹奏短笛，表现其天真烂漫。');
INSERT INTO "zhishitwo" VALUES (169, '“舟行碧波上，人在画中游。”这句诗描绘了怎样的画面？', '船在碧波上行驶，人仿佛在画中游览', '船在江上快速行驶', '人在岸边欣赏风景', '画家在船上作画', 'A', '描绘舟行碧波，人如置身画中的优美意境。');
INSERT INTO "zhishitwo" VALUES (170, '“故人西辞黄鹤楼，烟花三月下扬州。”中的“烟花”指的是什么？', '春天的繁花似锦（也指柳絮如烟、繁花似锦的景象）', '燃放的烟花', '烟火气息', '美丽的云彩', 'A', '李白《黄鹤楼送孟浩然之广陵》，“烟花”形容扬州三月繁花似锦的春景。');
INSERT INTO "zhishitwo" VALUES (171, '“孤帆远影碧空尽，唯见长江天际流。”中的“孤帆”比喻什么？', '一片帆布', '孤独的旅人', '远去的船', '单独的旗帜', 'C', '李白《黄鹤楼送孟浩然之广陵》，“孤帆”象征友人离去的孤独身影。');
INSERT INTO "zhishitwo" VALUES (172, '“相看两不厌，只有敬亭山。”表达了诗人怎样的情感？', '对友人的思念和期盼', '对人生的无奈和感慨', '对敬亭山的喜爱和敬仰', '对世事的厌倦和逃避', 'C', '李白《独坐敬亭山》，表达对敬亭山的喜爱与敬仰。');
INSERT INTO "zhishitwo" VALUES (173, '“众鸟高飞尽，孤云独去闲。”中的“孤云”表达了什么意思？', '孤独的云彩', '高远的志向', '自由的象征', '漂泊无依的感觉', 'A', '李白《独坐敬亭山》，“孤云”象征诗人的孤独与超脱。');
INSERT INTO "zhishitwo" VALUES (174, '“相看两不厌，唯有敬亭山。”这句诗出自哪位诗人的作品？', '李白', '杜甫', '王维', '孟浩然', 'A', '李白《独坐敬亭山》，表达对敬亭山的喜爱与敬仰。');
INSERT INTO "zhishitwo" VALUES (175, '“日照香炉生紫烟，遥看瀑布挂前川。”中的“香炉”指的是什么？', '祭祀用的香炉', '焚香的器具', '香炉峰', '装饰用的香炉', 'C', '李白《望庐山瀑布》，“香炉”指香炉峰，描绘瀑布的壮丽景象。');
INSERT INTO "zhishitwo" VALUES (176, '“飞流直下三千尺，疑是银河落九天。”用了什么修辞手法来描述瀑布？', '夸张比喻', '夸张', '拟人', '排比', 'B', '李白《望庐山瀑布》，用夸张手法突出瀑布的雄伟气势。');
INSERT INTO "zhishitwo" VALUES (177, '“春眠不觉晓，处处闻啼鸟。”中的“晓”指的是什么时候？', '中午', '早晨', '傍晚', '深夜', 'B', '孟浩然《春晓》，“晓”指早晨，描绘春晨的宁静与生机。');
INSERT INTO "zhishitwo" VALUES (178, '“举头望明月，低头思故乡。”表达了诗人怎样的情感？', '思乡之情', '对月亮的喜爱', '对夜晚的宁静感慨', '对故乡的厌恶', 'A', '李白《静夜思》，以明月寄寓对故乡的深切思念。');
INSERT INTO "zhishitwo" VALUES (179, '“独在异乡为异客，每逢佳节倍思亲。”中的“佳节”指的是什么节日？', '端午节', '中秋节', '春节', '重阳节', 'D', '王维《九月九日忆山东兄弟》，“佳节”指重阳节，表达对亲人的深切思念。');
INSERT INTO "zhishitwo" VALUES (180, '“遥知兄弟登高处，遍插茱萸少一人。”描述了怎样的场景？', '端午节赛龙舟吃粽子', '中秋节赏月吃月饼', '春节放鞭炮贴春联', '重阳节登高插茱萸', 'D', '王维《九月九日忆山东兄弟》，描绘重阳节登高插茱萸的传统习俗。');
INSERT INTO "zhishitwo" VALUES (181, '“故人具鸡黍，邀我至田家。”中的“鸡黍”指的是什么？', '鸡肉和黍米', '鸡肉和小米', '饭菜', '一种祭祀用品', 'C', '孟浩然《过故人庄》，“鸡黍”指农家待客的简朴饭食，体现田园情谊。');
INSERT INTO "zhishitwo" VALUES (182, '“绿树阴浓夏日长，楼台倒影入池塘。”描述了怎样的夏日景象？', '夏日暴雨，池塘水满', '夏日炎热，无处躲藏', '夏日悠长，景色宜人', '夏日黄昏，楼台寂静', 'C', '描绘夏日绿树成荫、楼台倒影的宁静美景。');
INSERT INTO "zhishitwo" VALUES (183, '“水晶帘动微风起，满架蔷薇一院香。”中的“水晶帘”指的是什么？', '透明的帘子（比喻）', '珍贵的珠宝帘子', '水晶做的帘子', '一种装饰物', 'A', '比喻透明的帘子，表现微风拂过帘子的轻盈与优雅。');
INSERT INTO "zhishitwo" VALUES (184, '“采菊东篱下，悠然见南山。”表达了诗人怎样的心境？', '忙碌奔波，心无旁骛', '淡泊名利，悠然自得', '忧伤惆怅，难以自拔', '激动兴奋，满怀期待', 'B', '陶渊明《饮酒》，描绘诗人隐居田园、悠然自得的生活态度。');
INSERT INTO "zhishitwo" VALUES (185, '“山重水复疑无路，柳暗花明又一村。”中的“柳暗花明”比喻什么？', '春天的美景', '困境中的希望', '柳树的暗淡和花的明亮', '山路的曲折和多变', 'A', '陆游《游山西村》，比喻绝处逢生、豁然开朗的转机。');
INSERT INTO "zhishitwo" VALUES (186, '“欲穷千里目，更上一层楼。”告诉我们什么道理？', '要不断努力才能成功', '站得高看得远', '做事要有恒心', '要珍惜时间', 'B', '王之涣《登鹳雀楼》，通过登高望远的场景，表达“站得高看得远”的哲理。');
INSERT INTO "zhishitwo" VALUES (187, '“黄河远上白云间，一片孤城万仞山。”中的“孤城”表达了什么？', '山峰的高耸', '城市的繁华', '边塞的孤寂', '黄河的壮阔', 'C', '王之涣《凉州词》，“孤城”指边疆的孤立城池，象征戍边将士的孤独与坚守。');
INSERT INTO "zhishitwo" VALUES (188, '“羌笛何须怨杨柳，春风不度玉门关。”中的“玉门关”在哪里？', '内蒙古自治区', '新疆维吾尔自治区', '甘肃省敦煌市附近', '青海省', 'C', '王之涣《凉州词》，“玉门关”是古代通往西域的重要关隘，位于今甘肃敦煌附近。');
INSERT INTO "zhishitwo" VALUES (189, '“黑云压城城欲摧，甲光向日金鳞开。”描述了怎样的战争场景？', '战争中的和平谈判', '战争中的惨烈场面', '战争胜利后的欢庆', '战争前夕的紧张气氛', 'D', '李贺《雁门太守行》，以“黑云压城”渲染战争前夕的紧张与压抑。');
INSERT INTO "zhishitwo" VALUES (190, '“角声满天秋色里，塞上燕脂凝夜紫。”中的“燕脂”指的是什么？', '胭脂（比喻血色）', '一种红色的花', '燕子的脂肪', '一种红色的颜料', 'A', '李贺《雁门太守行》，“燕脂”比喻战士的鲜血，描绘战争的惨烈。');
INSERT INTO "zhishitwo" VALUES (191, '“半卷红旗临易水，霜重鼓寒声不起。”中的“易水”与哪个历史人物有关？', '刘备', '项羽', '荆轲', '关羽', 'C', '李贺《雁门太守行》，“易水”与荆轲刺秦的典故相关，象征壮士的悲壮。');
INSERT INTO "zhishitwo" VALUES (192, '“报君黄金台上意，提携玉龙为君死。”中的“玉龙”指的是什么？', '宝剑（比喻）', '玉做的龙', '白色的龙', '一种珍贵的玉器', 'A', '李贺《雁门太守行》，“玉龙”比喻宝剑，表达战士的忠诚与英勇。');
INSERT INTO "zhishitwo" VALUES (193, '“风急天高猿啸哀，渚清沙白鸟飞回。”描述了怎样的秋日景象？', '秋日黄昏，落日余晖', '秋日晴朗，鸟语花香', '秋风萧瑟，猿啸声哀', '秋日雨后，清新宜人', 'C', '杜甫《登高》，描绘秋日风急天高、猿啸哀鸣的萧瑟景象。');
INSERT INTO "zhishitwo" VALUES (194, '“无边落木萧萧下，不尽长江滚滚来。”中的“落木”指的是什么？', '落下的木头', '落叶', '枯萎的树木', '被砍倒的树木', 'B', '杜甫《登高》，“落木”指落叶，表现秋日的萧瑟与壮阔。');
INSERT INTO "zhishitwo" VALUES (195, '“万里悲秋常作客，百年多病独登台。”表达了诗人怎样的感慨？', '事业有成，志得意满', '身体健康，心情愉悦', '漂泊异乡，身世悲凉', '家庭和睦，生活幸福', 'C', '杜甫《登高》，表达诗人漂泊异乡、身世悲凉的感慨。');
INSERT INTO "zhishitwo" VALUES (196, '“艰难苦恨繁霜鬓，潦倒新停浊酒杯。”中的“浊酒杯”象征了什么？', '诗人的豪情和壮志', '诗人的苦闷和无奈', '诗人的富贵和荣华', '诗人的清贫和简朴', 'B', '杜甫《登高》，“浊酒杯”象征诗人生活的困顿与内心的苦闷。');
INSERT INTO "zhishitwo" VALUES (197, '“昔人已乘黄鹤去，此地空余黄鹤楼。”中的“昔人”指的是谁？', '传说中的仙人', '某个具体的历史人物', '诗人自己', '诗人的朋友', 'A', '崔颢《黄鹤楼》，“昔人”指传说中的仙人，表达对历史与传说的感慨。');
INSERT INTO "zhishitwo" VALUES (198, '“黄鹤一去不复返，白云千载空悠悠。”表达了怎样的感慨？', '历史长河，波澜壮阔', '人生短暂，要珍惜当下', '时光流逝，物是人非', '历史长河，波澜壮阔', 'C', '崔颢《黄鹤楼》，表达对时光流逝、物是人非的感慨。');
INSERT INTO "zhishitwo" VALUES (199, '“晴川历历汉阳树，芳草萋萋鹦鹉洲。”中的“鹦鹉洲”在哪里？', '湖北省武汉市附近', '江苏省南京市附近', '浙江省杭州市附近', '四川省成都市附近', 'A', '崔颢《黄鹤楼》，“鹦鹉洲”位于武汉长江中，因祢衡《鹦鹉赋》得名。');
INSERT INTO "zhishitwo" VALUES (200, '“日暮乡关何处是？烟波江上使人愁。”中的“乡关”指的是什么？', '故乡', '乡镇的关口', '乡村的风景', '乡亲的情谊', 'A', '崔颢《黄鹤楼》，“乡关”指故乡，表达对故乡的思念与愁绪。');
INSERT INTO "zhishitwo" VALUES (201, '“月落乌啼霜满天，江枫渔火对愁眠。”描述了怎样的夜晚景象？', '月明星稀，渔火闪烁', '月落霜重，乌啼声凄', '月圆人聚，欢声笑语', '月黑风高，阴森恐怖', 'B', '张继《枫桥夜泊》，描绘月落乌啼、霜重渔火的凄凉夜景。');
INSERT INTO "zhishitwo" VALUES (202, '“姑苏城外寒山寺，夜半钟声到客船。”中的“寒山寺”在哪里？', '浙江省杭州市附近', '江苏省苏州市附近', '福建省福州市附近', '广东省广州市附近', 'B', '张继《枫桥夜泊》，“寒山寺”位于苏州，因张继诗而闻名。');
INSERT INTO "zhishitwo" VALUES (203, '“借问酒家何处有？牧童遥指杏花村。”中的“杏花村”指的是什么？', '一个卖酒的地方（泛指）', '一个具体的村庄名', '一片杏花林', '一个酿酒的作坊', 'A', '杜牧《清明》，“杏花村”泛指卖酒的地方，非特指某地。');
INSERT INTO "zhishitwo" VALUES (204, '“春种一粒粟，秋收万颗子。”这句诗出自哪位诗人？', '李白', '杜甫', '李绅', '王维', 'C', '李绅《悯农》，描绘农民辛勤耕作的场景。');
INSERT INTO "zhishitwo" VALUES (205, '“白日依山尽，黄河入海流”中的“海”指的是哪个海？', '渤海', '黄海', '东海', '南海', 'A', '王之涣《登鹳雀楼》，“海”指渤海，黄河最终注入渤海。');
INSERT INTO "zhishitwo" VALUES (206, '“窗含西岭千秋雪，门泊东吴万里船。”中的“西岭”指的是什么？', '西方的山岭', '西岭雪山', '一个具体的地名', '诗人的想象之景', 'B', '杜甫《绝句》，“西岭”指成都西面的岷山，以“千秋雪”象征其高峻与永恒。');
INSERT INTO "zhishitwo" VALUES (207, '“独怜幽草涧边生，上有黄鹂深树鸣。”表达了诗人怎样的情感？', '对涧边草的怜惜', '对孤独生活的感慨', '对黄鹂鸟的赞美', '对自然景色的喜爱', 'D', '韦应物《滁州西涧》，表达对涧边幽草的偏爱，体现其恬淡的心境。');
INSERT INTO "zhishitwo" VALUES (208, '“野径云俱黑，江船火独明。”描述了怎样的夜晚景象？', '夜晚野外，云雾缭绕，江船灯火明亮', '夜晚江面，波光粼粼', '夜晚山林，兽鸣声声', '夜晚城市，灯火辉煌', 'A', '杜甫《春夜喜雨》，描绘夜晚野外云雾笼罩、江船灯火闪烁的静谧景象。');
INSERT INTO "zhishitwo" VALUES (209, '“晓看红湿处，花重锦官城。”中的“锦官城”是哪里？', '西安', '重庆', '成都', '南京', 'C', '杜甫《春夜喜雨》，“锦官城”指成都，因蜀锦闻名。');
INSERT INTO "zhishitwo" VALUES (210, '“随风潜入夜，润物细无声。”这句诗描绘的是什么？', '冬雪', '夏雨', '秋雨', '春雨', 'D', '杜甫《春夜喜雨》，细腻描写春雨滋润万物的特点。');
INSERT INTO "zhishitwo" VALUES (211, '“接天莲叶无穷碧，映日荷花别样红。”描述了怎样的夏日景色？', '夏日田野，麦浪滚滚', '夏日海滩，沙细水清', '夏日山林，绿树成荫', '夏日荷塘，莲叶碧绿，荷花红艳', 'D', '杨万里《晓出净慈寺送林子方》，描绘西湖夏日莲叶与荷花的壮丽景色。');
INSERT INTO "zhishitwo" VALUES (212, '“明月几时有？把酒问青天。”这句词出自哪位词人？', '李清照', '辛弃疾', '苏轼', '柳永', 'C', '苏轼《水调歌头·明月几时有》，表达对人生与宇宙的哲思。');
INSERT INTO "zhishitwo" VALUES (213, '“大江东去，浪淘尽，千古风流人物。”中的“大江”指的是哪条江？', '珠江', '黄河', '长江', '淮河', 'C', '苏轼《念奴娇·赤壁怀古》，“大江”即长江，借江水咏叹历史兴亡。');
INSERT INTO "zhishitwo" VALUES (214, '“山随平野尽，江入大荒流。”描述了怎样的自然景观？', '平原广阔，江水静谧', '山峰耸立，江水湍急', '山峦随着平原的延伸而消失，江水流入广阔的原野', '山林茂密，江水环绕', 'C', '李白《渡荆门送别》，描绘山峦与平原、江水的壮阔景象。');
INSERT INTO "zhishitwo" VALUES (215, '“月下飞天镜，云生结海楼。”中的“海楼”指的是什么？', '海边的楼阁', '海市蜃楼', '海上的船只', '海中的仙山', 'B', '李白《渡荆门送别》，“海楼”比喻海市蜃楼，展现江景的奇幻与壮丽。');
INSERT INTO "zhishitwo" VALUES (216, '“仍怜故乡水，万里送行舟。”表达了诗人怎样的情感？', '对旅途的厌倦', '对故乡的依恋', '对行舟的喜爱', '对万里的向往', 'B', '李白《渡荆门送别》，表达对故乡的深情依恋。');
INSERT INTO "zhishitwo" VALUES (217, '“黄鹤楼中吹玉笛，江城五月落梅花。”中的“江城”是哪里？', '南京', '武汉', '杭州', '苏州', 'B', '李白《与史郎中钦听黄鹤楼上吹笛》，“江城”指武汉，因长江而得名。');
INSERT INTO "zhishitwo" VALUES (218, '“寒雨连江夜入吴，平明送客楚山孤。”中的“楚山”指的是哪里的山？', '江北的山', '具体的山名', '楚地的山,即江南的山', '楚文化的象征', 'C', '王昌龄《芙蓉楼送辛渐》，“楚山”泛指江南的山，象征送别时的孤寂与离愁。');
INSERT INTO "zhishitwo" VALUES (219, '“两个黄鹂鸣翠柳，一行白鹭上青天。”描述了怎样的春日景象？', '春日里，细雨绵绵', '春日里，桃花盛开', '春日里，黄鹂鸣叫，翠柳依依，白鹭飞翔', '春日里，寒风凛冽', 'C', '杜甫《绝句》，描绘春日黄鹂鸣柳、白鹭飞翔的生机盎然景象。');
INSERT INTO "zhishitwo" VALUES (220, '“窗前明月光，疑是地上霜。”中的“疑”字用得好在哪里？', '描绘了地上的霜白', '突出了月光的明亮', '表现了诗人的错觉和惊喜', '与“光”字相呼应', 'C', '李白《静夜思》，“疑”字生动表现诗人恍惚中将月光误认为霜的瞬间。');
INSERT INTO "zhishitwo" VALUES (221, '“举杯邀明月，对影成三人。”中的“三人”指的是谁？', '诗人、明月、酒友', '诗人、明月、诗人的影子', '诗人、明月、仆人', '诗人、两个朋友', 'B', '李白《月下独酌》，“三人”指诗人、明月及自己的影子，表现孤独中的自得其乐。');
INSERT INTO "zhishitwo" VALUES (222, '“此夜曲中闻折柳，何人不起故园情。”中的“折柳”有何寓意？', '折断柳枝玩耍', '离别、思乡', '柳枝的音乐美感', '柳树的生长习性', 'B', '李白《春夜洛城闻笛》，“折柳”象征离别与思乡，源于古代折柳送别的习俗。');
INSERT INTO "zhishitwo" VALUES (223, '“故人何不返，春华复应晚。”中的“春华”指的是什么？', '春天的美好时光', '春天的华丽装饰', '春天的温暖阳光', '青春年华', 'D', '“春华”比喻青春年华，表达对时光流逝的感慨。');
INSERT INTO "zhishitwo" VALUES (224, '“庐山秀出南斗傍，屏风九叠云锦张。”中的“南斗”指的是什么？', '南方的山峰', '南方的斗笠', '星宿名(天文学知识)', '南方的城市', 'C', '李白《庐山谣寄卢侍御虚舟》，“南斗”指南斗六星，象征庐山的雄伟与高远。');
INSERT INTO "zhishitwo" VALUES (225, '“天门中断楚江开，碧水东流至此回。”描述了怎样的自然景观？', '江水环绕，山峦叠嶂', '天门山高耸，江水静谧', '楚江宽阔，碧波荡漾', '天门山断开，楚江奔流，碧水在此回旋', 'D', '李白《望天门山》，描绘天门山断开、楚江奔流的壮丽景象。');
INSERT INTO "zhishitwo" VALUES (226, '“两岸猿声啼不住，轻舟已过万重山。”中的“万重山”指的是什么？', '一万座山峰', '连绵不断的山峰', '具体的山名', '山中的重重困难', 'B', '李白《早发白帝城》，“万重山”形容山峰连绵不断，展现三峡的险峻与壮阔。');
INSERT INTO "zhishitwo" VALUES (227, '“日出江花红胜火，春来江水绿如蓝。”中的“蓝”指的是什么颜色？', '绿色', '蓝色', '红色', '黄色', 'B', '白居易《忆江南》，“蓝”指蓝色，形容江水碧绿如蓝的美景。');
INSERT INTO "zhishitwo" VALUES (228, '“春风又绿江南岸，明月何时照我还。”中的“绿”字用得好在哪里？', '与“明月”形成对比', '表示颜色鲜艳', '形象地描绘了春天的生机', '突出了江南的景色美', 'C', '王安石《泊船瓜洲》，“绿”字化静为动，生动展现春风吹绿江南的动态美。');
INSERT INTO "zhishitwo" VALUES (229, '“独在异乡为异客，每逢佳节倍思亲。”中的“佳节”通常指的是哪个节日？', '重阳节', '中秋节', '端午节', '春节', 'A', '王维《九月九日忆山东兄弟》，“佳节”指重阳节，表达对亲人的深切思念。');
INSERT INTO "zhishitwo" VALUES (230, '“白日放歌须纵酒，青春作伴好还乡。”表达了诗人怎样的心情？', '豪情壮志，志在四方', '忧伤惆怅，不愿离乡', '平静淡然，随缘而安', '欢欣鼓舞，急于还乡', 'D', '杜甫《闻官军收河南河北》，表达诗人听闻安史之乱平定后的狂喜之情。');
INSERT INTO "zhishitwo" VALUES (231, '“野火烧不尽，春风吹又生。”中的“野火”比喻什么？', '顽强的生命力（反衬）', '实际的火灾', '野外的篝火', '愤怒的火焰', 'A', '白居易《赋得古原草送别》，以野火反衬野草的生命力，表达对友情的坚定信念。');
INSERT INTO "zhishitwo" VALUES (232, '“遥知兄弟登高处，遍插茱萸少一人。”描述的是哪个传统节日？', '清明节', '重阳节', '元宵节', '中秋节', 'B', '王维《九月九日忆山东兄弟》，描绘重阳节登高插茱萸的传统习俗。');
INSERT INTO "zhishitwo" VALUES (233, '“清明时节雨纷纷，路上行人欲断魂。”中的“断魂”指的是什么？', '迷路找不到方向', '灵魂离开身体', '形容极度悲伤', '雨水打湿衣裳', 'C', '杜牧《清明》，“断魂”形容清明扫墓时对逝去亲人的极度悲伤。');
INSERT INTO "zhishitwo" VALUES (234, '“煮豆燃豆萁，豆在釜中泣。”中的“豆”比喻什么？', '烹饪的食材', '实际的豆子', '豆科植物', '自己或兄弟', 'D', '曹植《七步诗》，以豆萁相煎喻兄弟相争，表达对亲情的珍视与无奈。');
INSERT INTO "zhishitwo" VALUES (235, '“不识庐山真面目，只缘身在此山中。”告诉我们什么道理？', '山中风景，独一无二', '身临其境，感受更深', '远距离看，更美', '当局者迷，旁观者清', 'D', '苏轼《题西林壁》，强调不同视角得出不同结论的哲理。');
INSERT INTO "zhishitwo" VALUES (236, '“春蚕到死丝方尽，蜡炬成灰泪始干。”中的“丝”暗指什么？', '丝线（物品）', '蚕丝（实际）', '思念或情感（比喻）', '音乐中的丝弦', 'C', '李商隐《无题》，以“丝”喻思念或情感，表达至死不渝的爱情。');
INSERT INTO "zhishitwo" VALUES (237, '“海上生明月，天涯共此时。”表达了诗人怎样的情感？', '对海洋的热爱', '对远方亲人的思念', '对明月的赞美', '对天涯的向往', 'B', '张九龄《望月怀远》，以明月寄托对远方亲人的思念。');
INSERT INTO "zhishitwo" VALUES (238, '“千山鸟飞绝，万径人踪灭。”描述了怎样的景象？', '热闹非凡的场景', '孤寂荒凉的景象', '生机勃勃的春天', '人来人往的街道', 'B', '柳宗元《江雪》，描绘寒冬万籁俱寂的苍茫雪景。');
INSERT INTO "zhishitwo" VALUES (239, '“孤舟蓑笠翁，独钓寒江雪。”中的“蓑笠翁”指的是什么？', '冬天的行人', '一般的渔夫', '江上的船夫', '披蓑戴笠的老渔翁', 'D', '柳宗元《江雪》，“蓑笠翁”指披蓑戴笠的老渔翁，象征诗人的孤傲与坚韧。');
INSERT INTO "zhishitwo" VALUES (240, '“月落乌啼霜满天，江枫渔火对愁眠。”中的“愁眠”表达了什么？', '对渔火的赞美', '对江枫的喜爱', '旅途的忧愁和孤寂', '对月亮的思念', 'C', '张继《枫桥夜泊》，“愁眠”表达诗人旅途中的孤寂与愁绪。');
INSERT INTO "zhishitwo" VALUES (241, '“黄河远上白云间，一片孤城万仞山。”中的“孤城”指的是什么？', '高山上的庙宇', '繁华的都市', '边塞的孤立城堡', '江边的小城', 'C', '王之涣《凉州词》，“孤城”指边疆的孤立城池，象征戍边将士的孤独与坚守。');
INSERT INTO "zhishitwo" VALUES (242, '“白日依山尽，黄河入海流。欲穷千里目，更上一层楼。”中的“楼”指什么？', '登高远望的楼阁', '普通的住宅楼', '城墙上的城楼', '山间的凉亭', 'A', '王之涣《登鹳雀楼》，通过登高望远的场景，表达“站得高看得远”的哲理。');
INSERT INTO "zhishitwo" VALUES (243, '“黑云压城城欲摧，甲光向日金鳞开。”中的“金鳞”指的是什么？', '金色的鱼鳞', '铠甲在日光下的反光', '黄色的龙鳞', '闪光的宝石', 'B', '李贺《雁门太守行》，“金鳞”比喻战士铠甲在日光下的反光，渲染战场肃杀氛围。');
INSERT INTO "zhishitwo" VALUES (244, '“柴门闻犬吠，风雪夜归人。”中的“归人”指的是谁？', '出门远行的人', '风雪中回家的旅人', '守在家中的人', '邻家的访客', 'B', '刘长卿《逢雪宿芙蓉山主人》，“归人”指在风雪夜归家的旅人，表现诗人对旅途艰辛的感慨。');
INSERT INTO "zhishitwo" VALUES (245, '“醉卧沙场君莫笑，古来征战几人回。”表达了诗人怎样的感慨？', '对胜利的喜悦', '对战争的无奈和悲壮', '对沙场的恐惧', '对征战的向往', 'B', '王翰《凉州词》，以豪放之语暗含对战争残酷的悲叹。');
INSERT INTO "zhishitwo" VALUES (246, '“秦时明月汉时关，万里长征人未还。”中的“长征”指的是什么？', '长途的征战或行军', '一次特定的战役', '边疆的防御工事', '古代的长跑比赛', 'A', '王昌龄《出塞》，“长征”指戍边将士长途征战，表达对边疆战士的同情。');
INSERT INTO "zhishitwo" VALUES (247, '“采菊东篱下，悠然见南山。”中的“南山”指的是哪里的山？', '南方的山脉', '特定的山名', '泛指远处的山', '诗人心中的山', 'C', '陶渊明《饮酒》，“南山”泛指远处的山，象征超脱尘世的隐逸心境。');
INSERT INTO "zhishitwo" VALUES (248, '“青海长云暗雪山，孤城遥望玉门关。”中的“玉门关”在哪里？', '青海省', '新疆维吾尔自治区', '内蒙古自治区', '甘肃省敦煌市附近', 'D', '王昌龄《从军行》，“玉门关”位于今甘肃敦煌，是古代通往西域的重要关隘。');
INSERT INTO "zhishitwo" VALUES (249, '“湖光秋月两相和，潭面无风镜未磨。”描述了怎样的湖面景色？', '湖面平静，月光皎洁', '湖面波光粼粼', '湖面风起浪涌', '湖面冰封如镜', 'A', '刘禹锡《望洞庭》，描绘秋夜洞庭湖的静谧与壮丽。');
INSERT INTO "zhishitwo" VALUES (250, '“东边日出西边雨，道是无晴却有晴。”中的“晴”暗指什么？', '天气的晴朗', '双关天气与情感', '心情的愉悦', '事情的顺利', 'B', '刘禹锡《竹枝词》，“晴”双关天气与情感，表达对爱情的微妙感受。');
INSERT INTO "zhishitwo" VALUES (251, '“曾经沧海难为水，除却巫山不是云。”中的“巫山”指的是哪里？', '湖北巫山', '湖南巫山', '重庆巫山', '云南巫山', 'C', '元稹《离思》，“巫山”指重庆巫山，象征诗人对爱情的忠贞与怀念。');
INSERT INTO "zhishitwo" VALUES (252, '“春风得意马蹄疾，一日看尽长安花。”中的“长安”是哪里？', '今洛阳', '今南京', '今杭州', '今西安', 'D', '孟郊《登科后》，“长安”指唐代都城，今陕西西安，表现诗人科举高中后的喜悦。');
INSERT INTO "zhishitwo" VALUES (253, '“夜来风雨声，花落知多少。”中的“知”字表达了诗人怎样的心情？', '对时间的感慨', '对风雨的畏惧', '对春天的期待', '对花落的惋惜和关切', 'D', '孟浩然《春晓》，“知”字表现诗人对花落的惋惜与对春天的关切。');
INSERT INTO "zhishitwo" VALUES (254, '“云想衣裳花想容，春风拂槛露华浓。”中的“裳”指的是什么？', '裙子', '衣裳', '裤子', '披风', 'B', '李白《清平调》，“裳”指上衣下裳，形容杨贵妃的华贵与美丽。');
INSERT INTO "zhishitwo" VALUES (255, '“独上高楼，望尽天涯路。”中的“天涯路”比喻什么？', '人生道路或远大前程', '实际的远方道路', '天边的云彩', '无尽的旅程', 'A', '晏殊《蝶恋花》，“天涯路”比喻人生道路或远大前程，表达对未来的期许。');
INSERT INTO "zhishitwo" VALUES (256, '“床前明月光，疑是地上霜。”中的“疑”字表达了诗人怎样的感受？', '悲伤难过', '惊喜交加', '疑惑不解', '平静淡然', 'C', '李白《静夜思》，“疑”字生动表现诗人恍惚中将月光误认为霜的瞬间。');
INSERT INTO "zhishitwo" VALUES (257, '“野旷天低树，江清月近人。”描述了怎样的自然景色？', '旷野辽阔，月映江中', '树林茂密，月光斑驳', '天空高远，树影婆娑', '江水浑浊，月影模糊', 'A', '孟浩然《宿建德江》，描绘旷野辽阔、月映江中的宁静夜景。');
INSERT INTO "zhishitwo" VALUES (258, '“洛阳亲友如相问，一片冰心在玉壶。”中的“冰心”比喻什么？', '高尚的品德', '冰冷的内心', '坚强的意志', '清白纯洁的心灵', 'D', '王昌龄《芙蓉楼送辛渐》，以“冰心在玉壶”自喻，表达自己高洁的品格。');
INSERT INTO "zhishitwo" VALUES (259, '“独怜幽草涧边生，上有黄鹂深树鸣。”中的“独怜”表达了什么？', '对幽草的特别喜爱', '对黄鹂的赞赏', '对涧边的留恋', '对深树的敬畏', 'A', '韦应物《滁州西涧》，“独怜”表达诗人对涧边幽草的偏爱，体现其恬淡的心境。');
INSERT INTO "zhishitwo" VALUES (260, '“故人已乘黄鹤去，此地空余黄鹤楼。”中的“故人”指的是谁？', '远方的客人', '死去的亲人', '老朋友或旧识', '诗人的自喻', 'C', '崔颢《黄鹤楼》，“故人”指老朋友或旧识，表达对时光流逝的感慨。');
INSERT INTO "zhishitwo" VALUES (261, '“山重水复疑无路，柳暗花明又一村。”表达了怎样的哲理？', '人生充满变数', '困境中仍有希望', '旅途的艰辛', '乡村的宁静美好', 'B', '陆游《游山西村》，比喻绝处逢生、豁然开朗的转机。');
INSERT INTO "zhishitwo" VALUES (262, '“风急天高猿啸哀，渚清沙白鸟飞回。”中的“啸哀”描述了什么？', '猿猴的悲鸣声', '风声的呼啸', '鸟儿的欢叫声', '水流的潺潺声', 'A', '杜甫《登高》，“啸哀”形容猿猴的悲鸣声，渲染秋日的萧瑟与凄凉。');
INSERT INTO "zhishitwo" VALUES (263, '“无边落木萧萧下，不尽长江滚滚来。”中的“萧萧”指的是什么？', '风吹过的声音', '落叶飘落的声音', '长江的波涛声', '诗人的叹息声', 'B', '杜甫《登高》，“萧萧”形容落叶飘落的声音，表现秋日的萧瑟与壮阔。');
INSERT INTO "zhishitwo" VALUES (264, '“明月几时有？把酒问青天。”中的“青天”指的是什么？', '蓝色的天幕', '青天大老爷', '天空', '高远的天地', 'C', '苏轼《水调歌头》，“青天”指天空，表达诗人对宇宙与人生的哲思。');
INSERT INTO "zhishitwo" VALUES (265, '“大江东去，浪淘尽，千古风流人物。”中的“风流人物”指的是谁？', '有才华的人', '风流倜傥的人', '历史上的英雄豪杰', '英俊潇洒的人', 'C', '苏轼《念奴娇·赤壁怀古》，“风流人物”指历史上的英雄豪杰，如周瑜等。');
INSERT INTO "zhishitwo" VALUES (266, '“人生自古谁无死，留取丹心照汗青。”中的“汗青”指的是什么？', '汗水', '史册', '青色的衣服', '古代的书籍', 'B', '文天祥《过零丁洋》，“汗青”指史册，表达诗人对忠诚与气节的坚守。');
INSERT INTO "zhishitwo" VALUES (267, '“千磨万击还坚劲，任尔东西南北风。”中的“坚劲”指的是什么？', '竹子的硬度', '身体的强壮', '坚韧不拔的意志', '风的力度', 'C', '郑板桥《竹石》，以竹子比喻坚韧不拔的精神。');
INSERT INTO "zhishitwo" VALUES (268, '“粉骨碎身浑不怕，要留清白在人间。”中的“清白”比喻什么？', '无辜的清白', '身体的清洁', '白色的衣物', '高尚的品德和节操', 'D', '于谦《石灰吟》，以石灰自喻，表达坚守清白、正直不阿的品质。');
INSERT INTO "zhishitwo" VALUES (269, '“不要人夸颜色好，只留清气满乾坤。”中的“清气”指的是什么？', '清新的空气或香气', '清高的气质', '清淡的颜色', '清廉的品德', 'D', '王冕《墨梅》，“清气”指梅花的清香，象征诗人高洁的品格。');
INSERT INTO "zhishitwo" VALUES (270, '“云翻墨未遮山，白雨跳珠乱入船。”中的“白雨”指的是什么？', '大雨如注，雨点如白珠', '白色的雨云', '雨后的清新空气', '雨中的白雾', 'A', '苏轼《六月二十七日望湖楼醉书》，“白雨”形容大雨点如白色珠子，渲染雨势的猛烈。');
INSERT INTO "zhishitwo" VALUES (271, '“黑云翻墨未遮山，白雨跳珠乱入船。”这句诗用了什么修辞手法？', '排比和对偶', '拟人和对比', '比喻和夸张', '借代和象征', 'C', '苏轼《六月二十七日望湖楼醉书》，用比喻和夸张描绘暴雨的壮观景象。');
INSERT INTO "zhishitwo" VALUES (272, '“接天莲叶无穷碧，映日荷花别样红。”中的“别样红”指的是什么？', '荷花的数量很多', '荷花的品种不同', '荷花在阳光下的特殊红艳', '荷花的颜色多变', 'C', '杨万里《晓出净慈寺送林子方》，“别样红”形容荷花在阳光下的鲜艳色彩。');
INSERT INTO "zhishitwo" VALUES (273, '“小荷才露尖尖角，早有蜻蜓立上头。”中的“尖尖角”指的是什么？', '荷花的嫩芽', '荷叶刚长出的尖角', '蜻蜓的停留处', '荷塘的边角', 'B', '杨万里《小池》，“尖尖角”指荷叶刚长出的尖角，展现初夏荷塘的生机。');
INSERT INTO "zhishitwo" VALUES (274, '“不知细叶谁裁出，二月春风似剪刀。”中的“剪刀”比喻什么？', '实际的剪刀', '春风的裁剪之力', '裁缝的手艺', '春天的象征', 'B', '贺知章《咏柳》，“剪刀”比喻春风的裁剪之力，描绘柳叶的精致。');
INSERT INTO "zhishitwo" VALUES (275, '“草长莺飞二月天，拂堤杨柳醉春烟。”中的“醉春烟”表达了什么？', '杨柳的生机勃勃', '春烟的浓郁', '杨柳在春烟中的迷醉姿态', '春天的迷人景色', 'C', '高鼎《村居》，“醉春烟”形容杨柳在春烟中的迷醉姿态，展现春天的生机。');
INSERT INTO "zhishitwo" VALUES (276, '“清明时节雨纷纷，路上行人欲断魂。”这句诗描绘了怎样的场景？', '清明时节的春光明媚', '清明时节的祭祀活动', '清明时节的雨中行人', '清明时节的寂静无声', 'C', '杜牧《清明》，描绘清明时节细雨纷纷、行人哀伤的景象。');
INSERT INTO "zhishitwo" VALUES (277, '“牧童骑黄牛，歌声振林樾。”中的“林樾”指的是什么？', '树林中的阴凉处', '树林的边缘', '高大的树木', '茂密的森林', 'A', '袁枚《所见》，“林樾”指树林中的阴凉处，烘托牧童的天真烂漫。');
INSERT INTO "zhishitwo" VALUES (278, '“遥望洞庭山水翠，白银盘里一青螺。”中的“青螺”指的是什么？', '山上的树木', '实际的青螺', '湖中的岛屿', '洞庭山在湖水中的倒影（比喻）', 'D', '刘禹锡《望洞庭》，“青螺”比喻湖中的君山，展现湖光山色的优美意境。');
INSERT INTO "zhishitwo" VALUES (279, '“湖光秋月两相和，潭面无风镜未磨。”中的“镜未磨”比喻什么？', '湖面像一面大镜子', '镜子尚未打磨', '湖面平静', '湖水清澈见底', 'C', '刘禹锡《望洞庭》，“镜未磨”比喻湖面平静如镜，展现秋夜洞庭湖的静谧。');
INSERT INTO "zhishitwo" VALUES (280, '“醉里挑灯看剑，梦回吹角连营。”中的“挑灯”指的是什么动作？', '拿起灯笼', '挑起灯芯，使灯更亮', '点亮灯火', '举高灯笼照看', 'B', '辛弃疾《破阵子·为陈同甫赋壮词以寄之》，“挑灯”指挑起灯芯，展现诗人壮志未酬的情怀。');
INSERT INTO "zhishitwo" VALUES (281, '“春种一粒粟，秋收万颗子。”中的“粟”指的是什么农作物？', '玉米', '大米', '小米', '小麦', 'C', '李绅《悯农》，“粟”指小米，描绘农民辛勤耕作的场景。');
INSERT INTO "zhishitwo" VALUES (282, '“白日放歌须纵酒，青春作伴好还乡。”中的“放歌”表达了什么心情？', '忧愁苦闷', '悲伤难过', '欢快愉悦', '平静淡然', 'C', '杜甫《闻官军收河南河北》，“放歌”表达诗人听闻安史之乱平定后的狂喜之情。');
INSERT INTO "zhishitwo" VALUES (283, '“野火烧不尽，春风吹又生。”中的“野火”象征了什么？', '毁灭性的力量', '生命的活力', '温暖的阳光', '柔和的春风', 'A', '白居易《赋得古原草送别》，以野火反衬野草的生命力，表达对友情的坚定信念。');
INSERT INTO "zhishitwo" VALUES (284, '“遥知兄弟登高处，遍插茱萸少一人。”中的“一人”指的是谁？', '诗人的家人', '诗人的兄弟', '诗人的朋友', '诗人自己', 'D', '王维《九月九日忆山东兄弟》，“一人”指诗人自己，表达未能与兄弟共度重阳的遗憾。');
INSERT INTO "zhishitwo" VALUES (285, '“独在异乡为异客，每逢佳节倍思亲。”中的“佳节”通常指的是什么节日？', '端午节', '中秋节', '春节', '重阳节', 'D', '王维《九月九日忆山东兄弟》，“佳节”指重阳节，表达对亲人的深切思念。');
INSERT INTO "zhishitwo" VALUES (286, '“黄河远上白云间，一片孤城万仞山。”中的“孤城”给人怎样的感觉？', '繁华热闹', '孤独荒凉', '生机勃勃', '安宁祥和', 'B', '王之涣《凉州词》，“孤城”指边疆的孤立城池，象征戍边将士的孤独与坚守。');
INSERT INTO "zhishitwo" VALUES (287, '“三万里河东入海，五千仞岳上摩天。”中的“岳”指的是什么？', '平原', '小山丘', '高山', '河流', 'C', '陆游《秋夜将晓出篱门迎凉有感》，“岳”指高山，如华山、泰山等，展现祖国山河的壮丽。');
INSERT INTO "zhishitwo" VALUES (288, '“儿童相见不相识，笑问客从何处来。”中的“客”指的是谁？', '诗人的朋友', '远方的客人', '诗人自己', '诗人的亲戚', 'C', '贺知章《回乡偶书》，“客”指诗人自己，表达久别归乡的感慨。');
INSERT INTO "zhishitwo" VALUES (289, '“绿树阴浓夏日长，楼台倒影入池塘。”中的“倒影”描述了什么景象？', '楼台在水中的影子', '楼台的影子在地上', '楼台被绿树遮挡', '楼台在阳光下闪耀', 'A', '描绘夏日绿树成荫、楼台倒影的宁静美景。');
INSERT INTO "zhishitwo" VALUES (290, '“秋风萧瑟天气凉，草木摇荡露为霜。”中的“萧瑟”表达了怎样的气氛？', '凄凉萧瑟', '欢快热烈', '平静安详', '生机勃勃', 'A', '描绘秋日风急天高、草木凋零的萧瑟景象。');
INSERT INTO "zhishitwo" VALUES (291, '“峨眉山月半轮秋，影入平羌江水流。”中的“平羌江”是哪条河？', '青衣江（古称平羌江）', '岷江', '嘉陵江', '乌江', 'A', '李白《峨眉山月歌》，“平羌江”即青衣江，展现峨眉山月的优美景色。');
INSERT INTO "zhishitwo" VALUES (292, '“夜发清溪向三峡，思君不见下渝州。”中的“渝州”是现在的哪里？', '武汉市', '成都市', '重庆市', '南京市', 'C', '李白《峨眉山月歌》，“渝州”即今重庆，表达对友人的思念。');
INSERT INTO "zhishitwo" VALUES (293, '“朝辞白帝彩云间，千里江陵一日还。”中的“白帝”指的是什么？', '白帝山', '白帝城', '白帝庙', '一个神话人物', 'B', '李白《早发白帝城》，“白帝”指白帝城，位于今重庆奉节，展现三峡的壮丽景色。');
INSERT INTO "zhishitwo" VALUES (294, '“两岸猿声啼不住，轻舟已过万重山。”中的“万重山”给人怎样的感觉？', '山峦重叠，连绵不绝', '山峰高耸，直插云霄', '山势平缓，连绵起伏', '山谷幽深，曲径通幽', 'A', '李白《早发白帝城》，“万重山”形容山峰连绵不断，展现三峡的险峻与壮阔。');
INSERT INTO "zhishitwo" VALUES (295, '“孤帆远影碧空尽，唯见长江天际流。”中的“孤帆”指的是什么？', '诗人的想象', '一片孤独的帆', '江面上的一个标志', '一艘远去的船', 'D', '李白《黄鹤楼送孟浩然之广陵》，“孤帆”象征友人离去的孤独身影。');
INSERT INTO "zhishitwo" VALUES (296, '“洛阳亲友如相问，一片冰心在玉壶。”这句诗用了什么修辞手法？', '比喻', '拟人', '夸张', '排比', 'A', '王昌龄《芙蓉楼送辛渐》，以“冰心在玉壶”自喻，表达自己高洁的品格。');
INSERT INTO "zhishitwo" VALUES (297, '“最是一年春好处，绝胜烟柳满皇都。”中的“皇都”是哪里？', '洛阳', '京城', '长安', '开封', 'B', '韩愈《早春呈水部张十八员外》，“皇都”指京城，赞美早春景色胜过繁华都城。');
INSERT INTO "zhishitwo" VALUES (298, '“草树知春不久归，百般红紫斗芳菲。”中的“斗芳菲”表达了什么？', '花草平静，无甚变化', '花草衰败，凄凉萧瑟', '花草争艳，生机勃勃', '花草怒放，但无人赏', 'C', '描绘春天花草争艳、生机勃勃的景象。');
INSERT INTO "zhishitwo" VALUES (299, '“杨花落尽子规啼，闻道龙标过五溪。”中的“子规”是什么鸟？', '杜鹃鸟', '喜鹊', '乌鸦', '鹧鸪', 'A', '李白《闻王昌龄左迁龙标遥有此寄》，“子规”即杜鹃鸟，象征哀愁与离别。');
INSERT INTO "zhishitwo" VALUES (300, '“我寄愁心与明月，随君直到夜郎西。”中的“夜郎”是哪里？', '诗人的想象之地', '一个神话中的地方', '一个遥远的城市', '古代夜郎国（今贵州等地）', 'D', '李白《闻王昌龄左迁龙标遥有此寄》，“夜郎”指古代夜郎国，表达对友人的思念。');
INSERT INTO "zhishitwo" VALUES (301, '“独怜幽草涧边生，上有黄鹂深树鸣。”中的“幽草”给人怎样的感觉？', '荒凉、萧瑟', '繁华、热闹', '清幽、静谧', '生机、勃勃', 'C', '韦应物《滁州西涧》，“幽草”象征清幽静谧的景致，体现诗人的恬淡心境。');
INSERT INTO "zhishitwo" VALUES (302, '“春潮带雨晚来急，野渡无人舟自横。”中的“野渡”指的是什么？', '江边的码头', '野外的桥梁', '郊外的渡口', '山间的小溪', 'C', '韦应物《滁州西涧》，“野渡”指郊外的渡口，描绘春雨后野渡无人的静谧景象。');
INSERT INTO "zhishitwo" VALUES (303, '“相看两不厌，只有敬亭山。”中的“敬亭山”在哪里？', '安徽省宣城市附近', '江苏省南京市附近', '浙江省杭州市附近', '福建省福州市附近', 'A', '李白《独坐敬亭山》，“敬亭山”位于安徽省宣城市，是著名的风景名胜区。');
INSERT INTO "zhishitwo" VALUES (304, '“孤山寺北贾亭西，水面初平云脚低。”中的“贾亭”是什么？', '一座亭子（西湖景观）', '一个地名', '一位诗人的名字', '一种建筑风格', 'A', '白居易《钱塘湖春行》，“贾亭”指西湖孤山寺北的一座亭子，是西湖著名景观之一。');
INSERT INTO "zhishitwo" VALUES (305, '“几处早莺争暖树，谁家新燕啄春泥。”中的“暖树”指的是什么？', '温暖的房间', '向阳的树', '热带的树木', '春天发芽的树', 'B', '白居易《钱塘湖春行》，“暖树”指向阳的树，描绘早春生机勃勃的景象。');
INSERT INTO "zhishitwo" VALUES (306, '“乱花渐欲迷人眼，浅草才能没马蹄。”这句诗用了什么修辞手法？', '夸张', '比喻', '拟人', '排比', 'C', '白居易《钱塘湖春行》，用拟人手法描绘春天花草的繁茂与生机。');
INSERT INTO "zhishitwo" VALUES (307, '“最爱湖东行不足，绿杨阴里白沙堤。”中的“白沙堤”是哪里？', '杭州西湖的白堤', '南京玄武湖的堤岸', '扬州瘦西湖的长堤', '苏州太湖的岸堤', 'A', '白居易《钱塘湖春行》，“白沙堤”指杭州西湖的白堤，是西湖著名景观之一。');
INSERT INTO "zhishitwo" VALUES (308, '“卷地风来忽吹散，望湖楼下水如天。”中的“望湖楼”在哪里？', '鄱阳湖边', '洞庭湖边', '太湖边', '杭州西湖边', 'D', '苏轼《六月二十七日望湖楼醉书》，“望湖楼”位于杭州西湖边，是观赏西湖的著名景点。');
INSERT INTO "zhishitwo" VALUES (309, '“毕竟西湖六月中，风光不与四时同。”中的“四时”指的是什么？', '四季', '四个时辰', '四年', '四个方向', 'A', '杨万里《晓出净慈寺送林子方》，“四时”指四季，强调西湖六月风光的独特与美好。');
INSERT INTO "zhishitwo" VALUES (310, '“接天莲叶无穷碧，映日荷花别样红。”中的“映日”表达了什么？', '荷花在阳光下更红', '荷叶像天空一样广阔', '荷花与太阳相映成趣', '荷叶遮挡了太阳', 'A', '杨万里《晓出净慈寺送林子方》，“映日”形容荷花在阳光下的鲜艳色彩。');
INSERT INTO "zhishitwo" VALUES (311, '“青山遮不住，毕竟东流去。”中的“东流”指的是什么？', '东风吹过', '人们向东迁移', '江水向东流', '青山向东延伸', 'C', '辛弃疾《菩萨蛮·书江西造口壁》，“东流”指江水向东流，象征历史的必然趋势。');
INSERT INTO "zhishitwo" VALUES (312, '“郁孤台下清江水，中间多少行人泪。”中的“郁孤台”在哪里？', '江西省赣州市', '福建省福州市', '广东省广州市', '湖南省长沙市', 'A', '辛弃疾《菩萨蛮·书江西造口壁》，“郁孤台”位于江西赣州，是著名的历史遗迹。');
INSERT INTO "zhishitwo" VALUES (313, '“西北望长安，可怜无数山。”中的“长安”是哪里？', '北京', '武汉', '成都', '西安', 'D', '辛弃疾《菩萨蛮·书江西造口壁》，“长安”指唐代都城，今陕西西安，象征国家的繁荣与统一。');
INSERT INTO "zhishitwo" VALUES (314, '“醉卧沙场君莫笑，古来征战几人回？”中的“沙场”指的是什么？', '沙漠', '沙地', '战场', '练兵场', 'C', '王翰《凉州词》，“沙场”指战场，表达对战争残酷的悲叹。');
INSERT INTO "zhishitwo" VALUES (315, '“生当作人杰，死亦为鬼雄。”这句诗出自哪位女诗人？', '李清照', '鱼玄机', '薛涛', '红玉', 'A', '李清照《夏日绝句》，以项羽宁死不屈的精神自勉，表达对英雄的崇敬。');
INSERT INTO "zhishitwo" VALUES (316, '“至今思项羽，不肯过江东。”中的“江东”指的是哪里？', '淮河以东地区', '黄河以东地区', '长江以南地区', '长江下游以东地区', 'D', '李清照《夏日绝句》，“江东”指长江下游以东地区，项羽兵败后不肯渡江的地方。');
INSERT INTO "zhishitwo" VALUES (317, '“白日依山尽，黄河入海流。”中的“依山尽”表达了什么景象？', '太阳落山，依山而没', '太阳升起，照亮山川', '太阳高悬，照耀大地', '太阳被云遮住，若隐若现', 'A', '王之涣《登鹳雀楼》，“依山尽”描绘太阳落山的壮丽景象。');
INSERT INTO "zhishitwo" VALUES (318, '“欲穷千里目，更上一层楼。”这句诗用了什么修辞手法来鼓励人？', '比喻', '夸张', '拟人', '借代', 'B', '王之涣《登鹳雀楼》，用夸张手法表达“站得高看得远”的哲理，鼓励人们不断进取。');
INSERT INTO "zhishitwo" VALUES (319, '“野径云俱黑，江船火独明。”中的“野径”指的是什么？', '山间的栈道', '田野中的大道', '郊外的小路', '江边的码头', 'C', '杜甫《春夜喜雨》，“野径”指郊外的小路，描绘夜晚野外的静谧景象。');
INSERT INTO "zhishitwo" VALUES (320, '“随风潜入夜，润物细无声。”中的“润”字用得好不好？为什么？', '一般，没什么特别', '不好，太普通了', '好，形象地描绘了雨水的滋润', '无需评价，只是描述', 'C', '杜甫《春夜喜雨》，“润”字细腻地描写了春雨滋润万物的特点，生动传神。');
INSERT INTO "zhishitwo" VALUES (321, '“夜来风雨声，花落知多少。”中的“知多少”表达了什么情感？', '对花落的惋惜和感慨', '对风雨的厌恶', '对春天的赞美', '对未知的好奇', 'A', '孟浩然《春晓》，“知多少”表达诗人对花落的惋惜与对春天的关切。');
INSERT INTO "zhishitwo" VALUES (322, '“孤帆远影碧空尽，唯见长江天际流。”这句诗中的“天际流”给人怎样的感觉？', '江水平静，如镜面般', '江水湍急，奔腾不息', '江水清澈，见底如镜', '江水与天相接，无边无际', 'D', '李白《黄鹤楼送孟浩然之广陵》，“天际流”描绘江水与天际相接的壮阔景象。');
INSERT INTO "zhishitwo" VALUES (323, '“窗含西岭千秋雪，门泊东吴万里船。”中的“西岭”指的是什么山？', '成都西边的山（泛指，可能指岷山等）', '西边的山岭（泛指）', '一座特定的山名', '东吴的山', 'A', '杜甫《绝句》，“西岭”指成都西面的岷山，以“千秋雪”象征其高峻与永恒。');
INSERT INTO "zhishitwo" VALUES (324, '“黄师塔前江水东，春光懒困倚微风。”中的“懒困”表达了诗人怎样的状态？', '忙碌疲惫，无暇赏春', '慵懒困倦，享受春光', '悲伤难过，对春无感', '兴奋激动，迎春而来', 'A', '杜甫《江畔独步寻花》，“懒困”表现诗人慵懒困倦，享受春光的惬意状态。');
INSERT INTO "zhishitwo" VALUES (325, '“桃花一簇开无主，可爱深红爱浅红？”中的“无主”指的是什么？', '桃花没有方向，随风飘荡', '桃花没有主人，可以随意采摘', '桃花自由生长，无人栽种', '桃花没有归属，象征孤独', 'C', '杜甫《江畔独步寻花》，“无主”形容桃花自由生长，无人栽种，展现自然之美。');
INSERT INTO "zhishitwo" VALUES (326, '“采得百花成蜜后，为谁辛苦为谁甜？”这句诗用了什么修辞手法来提问？', '设问', '反问', '比喻', '夸张', 'A', '罗隐《蜂》，用设问手法引发读者思考，表达对劳动人民辛勤付出的感慨。');
INSERT INTO "zhishitwo" VALUES (327, '“白日依山尽，黄河入海流”出自哪位诗人的作品？', '杜甫', '李白', '王维', '王之涣', 'D', '王之涣《登鹳雀楼》，描绘太阳落山、黄河奔流的壮丽景象。');
INSERT INTO "zhishitwo" VALUES (328, '“床前明月光，疑是地上霜”出自哪位诗人的作品？', '李白', '杜牧', '王之涣', '李清照', 'A', '李白《静夜思》，以明月寄寓对故乡的深切思念。');
INSERT INTO "zhishitwo" VALUES (329, '“无边落木萧萧下，不尽长江滚滚来”出自哪位诗人的作品？', '杜甫', '王之涣', '李白', '高适', 'A', '杜甫《登高》，描绘秋日落叶与长江奔流的壮阔景象。');
INSERT INTO "zhishitwo" VALUES (330, '“两岸猿声啼不住，轻舟已过万重山”出自哪位诗人的作品？', '王之涣', '李白', '王维', '杜牧', 'B', '李白《早发白帝城》，描绘三峡行舟的迅疾与壮丽。');
INSERT INTO "zhishitwo" VALUES (331, '“黄河远上白云间，一片孤城万仞山”出自哪位诗人的作品？', '王之涣', '王维', '高适', '李白', 'C', '王之涣《凉州词》，描绘边塞孤城的苍凉景象。');
INSERT INTO "zhishitwo" VALUES (332, '“桃花潭水深千尺，不及汪伦送我情”出自哪位诗人的作品？', '王之涣', '白居易', '李白', '杜牧', 'C', '李白《赠汪伦》，以桃花潭水比喻友情的深厚。');
INSERT INTO "zhishitwo" VALUES (333, '“春眠不觉晓，处处闻啼鸟”出自哪位诗人的作品？', '李白', '王之涣', '孟浩然', '杜甫', 'C', '孟浩然《春晓》，描绘春晨的宁静与生机。');
INSERT INTO "zhishitwo" VALUES (334, '“朝辞白帝彩云间，千里江陵一日还”出自哪位诗人的作品？', '王维', '李白', '孟浩然', '王之涣', 'B', '李白《早发白帝城》，描绘三峡行舟的迅疾与壮丽。');
INSERT INTO "zhishitwo" VALUES (335, '“大漠孤烟直，长河落日圆”出自哪位诗人的作品？', '王之涣', '高适', '王维', '李白', 'B', '王维《使至塞上》，描绘边塞大漠的壮丽景象。');
INSERT INTO "zhishitwo" VALUES (336, '“春江潮水连海平，海上明月共潮生”出自哪位诗人的作品？', '杜牧', '苏轼', '李白', '王维', 'B', '张若虚《春江花月夜》，描绘春江月夜的壮丽景象。');
INSERT INTO "zhishitwo" VALUES (337, '“举头望明月，低头思故乡”出自哪位诗人的作品？', '李白', '王之涣', '王维', '孟浩然', 'A', '李白《静夜思》，以明月寄寓对故乡的深切思念。');
INSERT INTO "zhishitwo" VALUES (338, '“天街小雨润如酥，草色遥看近却无”出自哪位诗人的作品？', '王维', '杜甫', '白居易', '孟浩然', 'A', '韩愈《早春呈水部张十八员外》，描绘早春细雨滋润的景象。');
INSERT INTO "zhishitwo" VALUES (339, '“青山遮不住，毕竟东流去”出自哪位诗人的作品？', '杜甫', '王维', '苏轼', '李白', 'C', '辛弃疾《菩萨蛮·书江西造口壁》，表达对历史必然趋势的感慨。');
INSERT INTO "zhishitwo" VALUES (340, '“明月几时有？把酒问青天”出自哪位诗人的作品？', '苏轼', '王维', '李白', '杜甫', 'A', '苏轼《水调歌头·明月几时有》，表达对人生与宇宙的哲思。');
INSERT INTO "zhishitwo" VALUES (341, '“不知细叶谁裁出，二月春风似剪刀”出自哪位诗人的作品？', '王维', '孟浩然', '李白', '贺知章', 'D', '贺知章《咏柳》，以春风比喻裁剪柳叶的力量。');
INSERT INTO "zhishitwo" VALUES (342, '“山中相送罢，日暮掩柴扉”出自哪位诗人的作品？', '王维', '杜甫', '李白', '王之涣', 'A', '王维《山中送别》，描绘送别友人的场景。');
INSERT INTO "zhishitwo" VALUES (343, '“夜泊牛渚怀古，月光如水照青楼”出自哪位诗人的作品？', '李白', '杜牧', '苏轼', '王之涣', 'B', '杜牧《夜泊牛渚怀古》，描绘月夜怀古的意境。');
INSERT INTO "zhishitwo" VALUES (344, '“大江东去，浪淘尽，千古风流人物”出自哪位诗人的作品？', '李白', '杜甫', '苏轼', '王维', 'C', '苏轼《念奴娇·赤壁怀古》，咏叹历史兴亡与英雄豪杰。');
INSERT INTO "zhishitwo" VALUES (345, '“东风不与周郎便，铜雀春深锁二乔”出自哪位诗人的作品？', '李白', '曹操', '杜甫', '苏轼', 'B', '杜牧《赤壁》，以历史典故表达对战争的反思。');
INSERT INTO "zhishitwo" VALUES (346, '“远看山有色，近听水无声”出自哪位诗人的作品？', '王维', '李白', '高适', '杜甫', 'A', '王维《画》，描绘山水画的意境。');
INSERT INTO "zhishitwo" VALUES (347, '“千山我独行，不必相送”出自哪位诗人的作品？', '王之涣', '王维', '高适', '李白', 'C', '李白《独行》，表达诗人独行的豪情与洒脱。');
INSERT INTO "zhishitwo" VALUES (348, '“海内存知己，天涯若比邻”出自哪位诗人的作品？', '王之涣', '王维', '李白', '孟浩然', 'C', '王勃《送杜少府之任蜀州》，表达友情的深厚与距离的无碍。');
INSERT INTO "zhishitwo" VALUES (349, '“独在异乡为异客，每逢佳节倍思亲”出自哪位诗人的作品？', '王之涣', '杜甫', '王维', '高适', 'B', '王维《九月九日忆山东兄弟》，表达对亲人的深切思念。');
INSERT INTO "zhishitwo" VALUES (350, '“生死契阔，与子成说”出自哪位诗人的作品？', '王之涣', '王维', '高适', '杜甫', 'C', '《诗经·邶风·击鼓》，表达对爱情的忠贞与承诺。');
INSERT INTO "zhishitwo" VALUES (351, '“会当凌绝顶，一览众山小”出自哪位诗人的作品？', '王之涣', '王维', '高适', '杜甫', 'C', '杜甫《望岳》，表达诗人对人生的豪情壮志。');
INSERT INTO "zhishitwo" VALUES (352, '“月黑雁飞高，单于夜遁逃”出自哪位诗人的作品？', '高适', '李白', '王之涣', '杜甫', 'A', '卢纶《塞下曲》，描绘边塞战争的紧张气氛。');
INSERT INTO "zhishitwo" VALUES (353, '“闲云潭影日悠悠，物换星移几度秋”出自哪位诗人的作品？', '王之涣', '王维', '高适', '杜甫', 'B', '王勃《滕王阁序》，表达对时光流逝的感慨。');
INSERT INTO "zhishitwo" VALUES (354, '“相见时难别亦难，东风无力百花残”出自哪位诗人的作品？', '白居易', '王之涣', '李清照', '苏轼', 'A', '李商隐《无题》，表达对爱情的无奈与哀愁。');
INSERT INTO "zhishitwo" VALUES (355, '“明月照大江，水清石出”出自哪位诗人的作品？', '王维', '杜甫', '李白', '高适', 'C', '李白《夜泊牛渚怀古》，描绘月夜江景的宁静与壮丽。');
INSERT INTO "zhishitwo" VALUES (356, '“忽如一夜春风来，千树万树梨花开”出自哪位诗人的作品？', '白居易', '王之涣', '李白', '杜甫', 'A', '岑参《白雪歌送武判官归京》，描绘冬日雪景的壮丽与生机。');
INSERT INTO "zhishitwo" VALUES (357, '“何日功成名遂了，薄暮花前空自惜”出自哪位诗人的作品？', '王之涣', '王维', '杜甫', '苏轼', 'D', '苏轼《临江仙·夜归临皋》，表达对人生功名的感慨与无奈。');
INSERT INTO "zhishitwo" VALUES (358, '“关山难越谁悲失路之人”出自哪位诗人的作品？', '王维', '王之涣', '杜甫', '白居易', 'C', '王勃《滕王阁序》，表达对人生困境的感慨与无奈。');
INSERT INTO "zhishitwo" VALUES (359, '“世事一场大梦，人生几度秋凉”出自哪位诗人的作品？', '苏轼', '白居易', '王维', '王之涣', 'A', '苏轼《西江月·世事一场大梦》，表达对人生无常的感慨。');
INSERT INTO "zhishitwo" VALUES (360, '“接天莲叶无穷碧，映日荷花别样红”出自哪位诗人的作品？', '王之涣', '李白', '苏轼', '白居易', 'C', '杨万里《晓出净慈寺送林子方》，描绘西湖夏日荷花的壮丽景色。');
INSERT INTO "zhishitwo" VALUES (361, '“空山不见人，但闻人语响”出自哪位诗人的作品？', '王维', '高适', '杜甫', '白居易', 'A', '王维《鹿柴》，描绘空山幽静的景致。');
INSERT INTO "zhishitwo" VALUES (362, '“落霞与孤鹜齐飞，秋水共长天一色”出自哪位诗人的作品？', '王之涣', '王维', '杜牧', '李白', 'B', '王勃《滕王阁序》，描绘秋日黄昏的壮丽景象。');
INSERT INTO "zhishitwo" VALUES (363, '“杨柳青青江水平，闻郎江上唱”出自哪位诗人的作品？', '李白', '王之涣', '白居易', '杜牧', 'C', '白居易《杨柳枝词》，描绘春日江边的生机与爱情。');
INSERT INTO "zhishitwo" VALUES (364, '“鸟宿池边树，僧敲月下门”出自哪位诗人的作品？', '王之涣', '王维', '白居易', '杜牧', 'B', '贾岛《题李凝幽居》，描绘月夜幽静的景致。');
INSERT INTO "zhishitwo" VALUES (365, '“两情若是久长时，又岂在朝朝暮暮”出自哪位诗人的作品？', '王之涣', '王维', '李清照', '杜甫', 'C', '秦观《鹊桥仙》，表达对爱情的坚贞与承诺。');
INSERT INTO "zhishitwo" VALUES (366, '“孤帆远影碧空尽，唯见长江天际流”出自哪位诗人的作品？', '李白', '王维', '王之涣', '杜甫', 'A', '李白《黄鹤楼送孟浩然之广陵》，描绘送别友人的深情。');
INSERT INTO "zhishitwo" VALUES (367, '“不识庐山真面目，只缘身在此山中”出自哪位诗人的作品？', '王维', '李白', '高适', '杜甫', 'A', '苏轼《题西林壁》，表达“当局者迷，旁观者清”的哲理。');
INSERT INTO "zhishitwo" VALUES (368, '“白云千载空悠悠，晴川历历汉阳树”出自哪位诗人的作品？', '崔颢', '高适', '李白', '杜甫', 'A', '崔颢《黄鹤楼》，表达对时光流逝的感慨与怀古之情。');
INSERT INTO "zhishitwo" VALUES (369, '“塞下曲，明月夜，短长亭”出自哪位诗人的作品？', '王之涣', '王维', '王之涣', '李白', 'A', '卢纶《塞下曲》，描绘边塞月夜的景象。');
INSERT INTO "zhishitwo" VALUES (370, '“池塘水清，树影碧，池上烟茫茫”出自哪位诗人的作品？', '杜甫', '王之涣', '王维', '白居易', 'D', '白居易《池上》，描绘池塘水清、树影碧绿的静谧景象。');
INSERT INTO "zhishitwo" VALUES (371, '“水何澹澹，山岛竦峙”出自哪位诗人的作品？', '王维', '王之涣', '李白', '高适', 'A', '曹操《观沧海》，描绘大海的壮阔与山岛的雄伟。');
INSERT INTO "zhishitwo" VALUES (372, '“蜀道难，难于上青天”出自哪位诗人的作品？', '王维', '王之涣', '李白', '杜甫', 'C', '李白《蜀道难》，描绘蜀道的险峻与艰难。');
INSERT INTO "zhishitwo" VALUES (373, '“金戈铁马，气吞万里如虎”出自哪位诗人的作品？', '王维', '李白', '王之涣', '岳飞', 'D', '辛弃疾《永遇乐·京口北固亭怀古》，描绘战场的气势与豪情。');
INSERT INTO "zhishitwo" VALUES (374, '“青青园中葵，朝露待日晞”出自哪位诗人的作品？', '王之涣', '李白', '孟浩然', '王维', 'C', '《长歌行》，描绘春日园中葵花的生机与希望。');
INSERT INTO "zhishitwo" VALUES (375, '“早发白帝城，晨光照大地”出自哪位诗人的作品？', '王维', '李白', '高适', '杜甫', 'B', '李白《早发白帝城》，描绘三峡行舟的迅疾与壮丽。');
INSERT INTO "zhishitwo" VALUES (376, '“天阶夜色凉如水，卧看牵牛织女星”出自哪位诗人的作品？', '王维', '李白', '杜甫', '白居易', 'A', '杜牧《秋夕》，描绘秋夜星空的宁静与浪漫。');
INSERT INTO "zhishitwo" VALUES (377, '“长安一片月，万户捣衣声”出自哪位诗人的作品？', '王维', '王之涣', '高适', '李白', 'B', '李白《子夜吴歌·秋歌》，描绘长安秋夜的景象。');
INSERT INTO "zhishitwo" VALUES (378, '“万里悲秋常作客，百年多病独登台”出自哪位诗人的作品？', '王之涣', '杜甫', '高适', '王维', 'B', '杜甫《登高》，表达诗人漂泊异乡、身世悲凉的感慨。');
INSERT INTO "zhishitwo" VALUES (379, '“飞流直下三千尺，疑是银河落九天”出自哪位诗人的作品？', '王维', '李白', '高适', '杜甫', 'B', '李白《望庐山瀑布》，用夸张手法描绘瀑布的雄伟气势。');
INSERT INTO "zhishitwo" VALUES (380, '“一片孤城万仞山，黄河远上白云间”出自哪位诗人的作品？', '王之涣', '王维', '李白', '高适', 'C', '王之涣《凉州词》，描绘边塞孤城的苍凉景象。');
INSERT INTO "zhishitwo" VALUES (381, '“千里江陵一日还，朝辞白帝彩云间”出自哪位诗人的作品？', '李白', '王维', '杜甫', '王之涣', 'A', '李白《早发白帝城》，描绘三峡行舟的迅疾与壮丽。');
INSERT INTO "zhishitwo" VALUES (382, '“树树皆秋色，山山唯落晖”出自哪位诗人的作品？', '王维', '李白', '高适', '王之涣', 'A', '王绩《野望》，描绘秋日山野的萧瑟与壮丽。');
INSERT INTO "zhishitwo" VALUES (383, '“欲穷千里目，更上一层楼”出自哪位诗人的作品？', '王维', '王之涣', '高适', '杜甫', 'C', '王之涣《登鹳雀楼》，表达“站得高看得远”的哲理。');
INSERT INTO "zhishitwo" VALUES (384, '“离离原上草，一岁一枯荣”出自哪位诗人的作品？', '王之涣', '高适', '白居易', '王维', 'C', '白居易《赋得古原草送别》，描绘草原的生机与生命的循环。');
INSERT INTO "zhishitwo" VALUES (385, '“故人西辞黄鹤楼，烟花三月下扬州”出自哪位诗人的作品？', '王之涣', '杜甫', '李白', '王维', 'C', '李白《黄鹤楼送孟浩然之广陵》，描绘送别友人的深情。');
INSERT INTO "zhishitwo" VALUES (386, '“夜来风雨声，花落知多少”出自哪位诗人的作品？', '杜甫', '高适', '王之涣', '孟浩然', 'D', '孟浩然《春晓》，描绘春夜风雨后花落的景象。');
INSERT INTO "zhishitwo" VALUES (387, '“红豆生南国，春来发几枝”出自哪位诗人的作品？', '王维', '王之涣', '杜甫', '刘禹锡', 'D', '王维《相思》，以红豆象征爱情与思念。');
INSERT INTO "zhishitwo" VALUES (388, '“君自故乡来，应知故乡事”出自哪位诗人的作品？', '王维', '杜甫', '高适', '杨万里', 'B', '王维《杂诗》，表达对故乡的思念与关切。');
INSERT INTO "zhishitwo" VALUES (389, '“商女不知亡国恨，隔江犹唱后庭花”出自哪位诗人的作品？', '王之涣', '王维', '杜甫', '李煜', 'D', '杜牧《泊秦淮》，借商女讽刺统治者沉迷享乐。');
INSERT INTO "zhishitwo" VALUES (390, '“花开堪折直须折，莫待无花空折枝”出自哪位诗人的作品？', '李白', '王维', '杜甫', '贺知章', 'D', '杜秋娘《金缕衣》，劝诫人们珍惜时光与机会。');
INSERT INTO "zhishitwo" VALUES (391, '“故乡何处是，忘了除非醉”出自哪位诗人的作品？', '王维', '王之涣', '李白', '高适', 'C', '李清照《如梦令》，表达对故乡的深切思念。');
INSERT INTO "zhishitwo" VALUES (392, '“生当作人杰，死亦为鬼雄”出自哪位诗人的作品？', '司马光', '李白', '王维', '杜甫', 'B', '李清照《夏日绝句》，以项羽自勉，表达对英雄的崇敬。');
INSERT INTO "zhishitwo" VALUES (393, '“人生自古谁无死，留取丹心照汗青”出自哪位诗人的作品？', '王之涣', '文天祥', '李白', '王维', 'B', '文天祥《过零丁洋》，表达对忠诚与气节的坚守。');
INSERT INTO "zhishitwo" VALUES (394, '“海上生明月，天涯共此时”出自哪位诗人的作品？', '王之涣', '杜甫', '李白', '苏轼', 'D', '张九龄《望月怀远》，以明月寄托对远方亲人的思念。');
INSERT INTO "zhishitwo" VALUES (395, '“野火烧不尽，春风吹又生”出自哪位诗人的作品？', '王之涣', '白居易', '杜甫', '王维', 'C', '白居易《赋得古原草送别》，以野草的生命力表达对友情的坚定信念。');
INSERT INTO "zhishitwo" VALUES (396, '“千里之堤毁于蚁穴”出自哪位诗人的作品？', '王维', '王之涣', '苏轼', '孟浩然', 'C', '《韩非子·喻老》，比喻小问题可能导致大灾难。');
